mapper.update:
UPDATE POSTS /* QueryID : com.demo.proworks.domain.post.updatePost */ SET JOB_POSTING_ID = #{jobPostingId}
, COMPANY_ID = #{companyId} , TITLE = #{title} , DESCRIPTION = #{description} , EXPERIENCE_LEVEL = #{experienceLevel} , SALARY_RANGE = #{salaryRange} , LOCATION = #{location} , WORK_TYPE = #{workType} , PREFERRED_DEVELOPER_TYPES = #{preferredDeveloperTypes} , EXPIRES_AT = #{expiresAt} , POSTED_AT = #{postedAt} , STATUS = #{status}
WHERE JOB_POSTING_ID = #{jobPostingId}

mapper.select:
SELECT /* QueryID : com.demo.proworks.domain.post.selectListPost */ JOB_POSTING_ID as jobPostingId, COMPANY_ID as companyId, TITLE as title, DESCRIPTION as description, EXPERIENCE_LEVEL as experienceLevel, SALARY_RANGE as salaryRange, LOCATION as location, WORK_TYPE as workType, PREFERRED_DEVELOPER_TYPES as preferredDeveloperTypes, EXPIRES_AT as expiresAt, POSTED_AT as postedAt, STATUS as status FROM POSTS WHERE 1=1
AND COMPANY_ID = #{companyId}
ORDER BY POSTED_AT DESC

