scwin.startJourney = function() {
    var isLoggedIn = dmp_tutorial.get("isLoggedIn");
    if (isLoggedIn === "true") {
        // Î°úÍ∑∏Ïù∏Îêú ÏÇ¨Ïö©ÏûêÎäî Î©îÏù∏ ÌéòÏù¥ÏßÄÎ°ú
        scwin.goToMainPage();
    } else {
        // ÎπÑÎ°úÍ∑∏Ïù∏ ÏÇ¨Ïö©ÏûêÎäî ÌöåÏõêÍ∞ÄÏûÖÏúºÎ°ú
        scwin.goToSignup();
    }
};

// Îç∞Î™® Í∏∞Îä•Îì§
scwin.demoMbtiQuestion = function() {
    var options = document.querySelectorAll('.demo-mbti-option');
    options.forEach(function(option) {
        option.addEventListener('click', function() {
            options.forEach(function(opt) {
                opt.classList.remove('selected');
            });
            this.classList.add('selected');
        });
    });
};

scwin.demoCodeUpload = function() {
    var uploadArea = document.querySelector('.demo-upload-area');
    if (uploadArea) {
        uploadArea.classList.add('active');
        setTimeout(function() {
            uploadArea.classList.remove('active');
        }, 1500);
    }
};
