head.script:
scwin.onpageload = function() {

    // 초기화 함수 실행
    scwin.initializeTutorial();
};

// 튜토리얼 초기화
scwin.initializeTutorial = function() {
    // 여러 번 시도하여 안정성 확보
    var attempts = 0;
    var maxAttempts = 10;

    var init = function() {
        attempts++;
        console.log("초기화 시도:", attempts);

        var firstStep = document.getElementById('step-0');
        if (firstStep || attempts >= maxAttempts) {
            console.log("초기화 성공, 첫 번째 단계:", firstStep);

            // 로그인 상태 확인
            scwin.checkLoginStatus();

            // 첫 번째 단계로 시작
            scwin.showStep(0);

            return;
        }

        // 100ms 후 재시도
        setTimeout(init, 100);
    };

    // 즉시 시도
    init();
};

// 로그인 상태 확인
scwin.checkLoginStatus = function() {
    var userInfoCookie = WebSquare.cookie.getCookie("userInfo");
    var isLoggedIn = userInfoCookie ? true : false;
    dmp_tutorial.set("isLoggedIn", isLoggedIn ? "true" : "false");

    if (isLoggedIn) {
        // 로그인된 사용자 정보 표시
        try {
            var userInfo = JSON.parse(userInfoCookie);
            var welcomeElement = document.querySelector('.welcome-message');
            if (welcomeElement && userInfo.name) {
                welcomeElement.textContent = userInfo.name + "님, 환영합니다!";
            }
        } catch (e) {
            console.error("사용자 정보 파싱 오류:", e);
        }
    }
};

// 단계 표시
scwin.showStep = function(stepIndex) {
    console.log("showStep 호출:", stepIndex);
    dmp_tutorial.set("currentStep", stepIndex);

    // DOM 요소 확인 후 실행
    setTimeout(function() {
        // 모든 단계에서 active 클래스 제거
        var allSteps = document.querySelectorAll('.tutorial-step');
        console.log("찾은 단계 수:", allSteps.length);
        for (var i = 0; i < allSteps.length; i++) {
            allSteps[i].classList.remove('active');
            allSteps[i].style.display = 'none';
        }

        // 현재 단계에 active 클래스 추가 (여러 방법으로 시도)
        var currentStepElement = null;

        // 방법 1: ID로 찾기
        currentStepElement = document.getElementById('step-' + stepIndex);

        // 방법 2: 클래스로 찾기
        if (!currentStepElement) {
            currentStepElement = document.querySelector('.step-' + stepIndex);
        }

        // 방법 3: 인덱스로 찾기
        if (!currentStepElement && allSteps[stepIndex]) {
            currentStepElement = allSteps[stepIndex];
        }

        console.log("현재 단계 요소:", currentStepElement);
        if (currentStepElement) {
            currentStepElement.classList.add('active');
            currentStepElement.style.display = 'block';
            console.log("단계", stepIndex, "표시됨");
        } else {
            console.error("단계 요소를 찾을 수 없음:", 'step-' + stepIndex);
        }

        // 버튼 상태 업데이트
        scwin.updateButtons();
        scwin.updateProgress();

        // 단계별 특별 처리
        scwin.handleStepSpecialActions(stepIndex);
    }, 50);
};

// 단계별 특별 처리
scwin.handleStepSpecialActions = function(stepIndex) {
    switch(stepIndex) {
        case 2: // MBTI 성향 분석 단계
            scwin.animateMbtiCards();
            break;
        case 3: // 코드 분석 단계
            scwin.animateCodeAnalysis();
            break;
        case 4: // 매칭 시스템 단계
            scwin.animateMatching();
            break;
    }
};

// MBTI 카드 애니메이션
scwin.animateMbtiCards = function() {
    setTimeout(function() {
        var mbtiCards = document.querySelectorAll('.mbti-demo-card');
        mbtiCards.forEach(function(card, index) {
            setTimeout(function() {
                card.style.transform = 'scale(1)';
                card.style.opacity = '1';
            }, index * 200);
        });
    }, 300);
};

// 코드 분석 애니메이션
scwin.animateCodeAnalysis = function() {
    setTimeout(function() {
        var codeUpload = document.querySelector('.code-upload-demo');
        if (codeUpload) {
            codeUpload.classList.add('uploading');

            setTimeout(function() {
                codeUpload.classList.remove('uploading');
                codeUpload.classList.add('completed');

                var resultElement = document.querySelector('.code-result-demo');
                if (resultElement) {
                    resultElement.style.display = 'block';
                    resultElement.style.animation = 'fadeInUp 0.5s ease';
                }
            }, 2000);
        }
    }, 500);
};

// 매칭 애니메이션
scwin.animateMatching = function() {
    setTimeout(function() {
        var matchingElements = document.querySelectorAll('.matching-animation');
        matchingElements.forEach(function(element, index) {
            setTimeout(function() {
                element.style.opacity = '1';
                element.style.transform = 'translateY(0)';
            }, index * 400);
        });
    }, 300);
};

// 다음 단계
scwin.nextStep = function() {
    var currentStep = dmp_tutorial.get("currentStep");
    var totalSteps = dmp_tutorial.get("totalSteps");

    if (currentStep < totalSteps - 1) {
        scwin.showStep(currentStep + 1);
    }
};

// 이전 단계
scwin.prevStep = function() {
    var currentStep = dmp_tutorial.get("currentStep");

    if (currentStep > 0) {
        scwin.showStep(currentStep - 1);
    }
};

// 특정 단계로 이동
scwin.goToStep = function(stepIndex) {
    scwin.showStep(stepIndex);
};

// 버튼 상태 업데이트
scwin.updateButtons = function() {
    var currentStep = dmp_tutorial.get("currentStep");
    var totalSteps = dmp_tutorial.get("totalSteps");

    var prevBtn = document.getElementById('btn-prev');
    var nextBtn = document.getElementById('btn-next');
    var startBtn = document.getElementById('btn-start');

    if (prevBtn) {
        prevBtn.disabled = (currentStep === 0);
        prevBtn.style.opacity = (currentStep === 0) ? '0.5' : '1';
    }

    if (nextBtn) {
        if (currentStep === totalSteps - 1) {
            nextBtn.style.display = 'none';
            if (startBtn) startBtn.style.display = 'inline-block';
        } else {
            nextBtn.style.display = 'inline-block';
            if (startBtn) startBtn.style.display = 'none';
        }
    }
};

// 진행률 업데이트
scwin.updateProgress = function() {
    var currentStep = dmp_tutorial.get("currentStep");
    var totalSteps = dmp_tutorial.get("totalSteps");
    var progressPercent = ((currentStep + 1) / totalSteps) * 100;

    var progressBar = document.querySelector('.progress-bar-fill');
    var progressText = document.querySelector('.progress-text');

    if (progressBar) {
        progressBar.style.width = progressPercent + '%';
    }

    if (progressText) {
        progressText.textContent = (currentStep + 1) + ' / ' + totalSteps;
    }

    // 단계 인디케이터 업데이트
    var indicators = document.querySelectorAll('.step-indicator');
    indicators.forEach(function(indicator, index) {
        if (index <= currentStep) {
            indicator.classList.add('completed');
        } else {
            indicator.classList.remove('completed');
        }

        if (index === currentStep) {
            indicator.classList.add('current');
        } else {
            indicator.classList.remove('current');
        }
    });
};

// 실제 기능으로 이동하는 함수들
scwin.goToSignup = function() {
    var isLoggedIn = dmp_tutorial.get("isLoggedIn");
    if (isLoggedIn === "true") {
        $c.win.alert("이미 로그인되어 있습니다!");
        return;
    }
    $p.url("../user/Signup.xml");
};

scwin.goToMbtiTest = function() {
    $p.url("../survey/SurveyMbti.xml");
};

scwin.goToCodeAnalysis = function() {
    $p.url("../user/CodeAnalysisMain.xml");
};

scwin.goToMainPage = function() {
    $p.url("../common/MainPage.xml");
};

scwin.goToJobList = function() {
    $p.url("../post/PostList.xml");
};

scwin.startJourney = function() {
    var isLoggedIn = dmp_tutorial.get("isLoggedIn");
    if (isLoggedIn === "true") {
        // 로그인된 사용자는 메인 페이지로
        scwin.goToMainPage();
    } else {
        // 비로그인 사용자는 회원가입으로
        scwin.goToSignup();
    }
};

// 데모 기능들
scwin.demoMbtiQuestion = function() {
    var options = document.querySelectorAll('.demo-mbti-option');
    options.forEach(function(option) {
        option.addEventListener('click', function() {
            options.forEach(function(opt) {
                opt.classList.remove('selected');
            });
            this.classList.add('selected');
        });
    });
};

scwin.demoCodeUpload = function() {
    var uploadArea = document.querySelector('.demo-upload-area');
    if (uploadArea) {
        uploadArea.classList.add('active');
        setTimeout(function() {
            uploadArea.classList.remove('active');
        }, 1500);
    }
};
