/**
* ë°˜ì‘í˜• ê°€ìš´ë° ì •ë ¬ ìœ„ì¹˜ ë° í¬ê¸° ê³„ì‚° ë©”ì„œë“œ
* @param {Object} options - ì„¤ì • ì˜µì…˜
* @param {number} options.maxWidth - ìµœëŒ€ ë„ˆë¹„ (ê¸°ë³¸ê°’: 800)
* @param {number} options.minWidth - ìµœì†Œ ë„ˆë¹„ (ê¸°ë³¸ê°’: 400)
* @param {number} options.maxHeight - ìµœëŒ€ ë†’ì´ (ê¸°ë³¸ê°’: 600)
* @param {number} options.minHeight - ìµœì†Œ ë†’ì´ (ê¸°ë³¸ê°’: 300)
* @param {number} options.widthRatio - í™”ë©´ ëŒ€ë¹„ ë„ˆë¹„ ë¹„ìœ¨ (ê¸°ë³¸ê°’: 0.8)
* @param {number} options.heightRatio - í™”ë©´ ëŒ€ë¹„ ë†’ì´ ë¹„ìœ¨ (ê¸°ë³¸ê°’: 0.8)
* @returns {Object} {width, height, left, top} ê°’ì„ í¬í•¨í•œ ê°ì²´
*/
scwin.getResponsiveCenterPosition = function (options) {
    options = options || {};

    var browserWidth = window.innerWidth || document.documentElement.clientWidth;
    var browserHeight = window.innerHeight || document.documentElement.clientHeight;

    // í™”ë©´ í¬ê¸°ì˜ ë¹„ìœ¨ë¡œ íŒì—… í¬ê¸° ì„¤ì • (ìµœëŒ€/ìµœì†Œ í¬ê¸° ì œí•œ)
    var popupWidth = Math.min(
        options.maxWidth || 800,
        Math.max(options.minWidth || 400, browserWidth * (options.widthRatio || 0.8))
    );
    var popupHeight = Math.min(
        options.maxHeight || 600,
        Math.max(options.minHeight || 300, browserHeight * (options.heightRatio || 0.8))
    );

    // ê°€ìš´ë° ìœ„ì¹˜ ê³„ì‚°
    var leftPos = (browserWidth - popupWidth) / 2;
    var topPos = (browserHeight - popupHeight) / 2;

    return {
        width: popupWidth,
        height: popupHeight,
        left: leftPos,
        top: topPos
    };
};

