};

/**
 * íšŒì‚¬ ëª©ë¡ ì¡°íšŒ ì„±ê³µ ì½œë°±
 */
scwin.sbm_selectCompanyList_submitdone = function (e) {
        // ì‘ë‹µ ë°ì´í„° í™•ì¸
        var elData = e.responseJSON.elData;
        var elHeader = e.responseJSON.elHeader;

        if (elHeader && elHeader.resSuc === false) {
            $c.win.alert(`ì—ëŸ¬ì½”ë“œ: ${elHeader.resCode}\nì—ëŸ¬ë©”ì‹œì§€: ${elHeader.resMsg}`);
            return false;
        }

        // ì´ íšŒì‚¬ ìˆ˜ ì—…ë°ì´íŠ¸
        var totalCount = dlt_company_list.getRowCount();
        console.log('ë°ì´í„° ë¡œë“œ ì™„ë£Œ. ì´ íšŒì‚¬ ìˆ˜:', totalCount);

        // txt_company_count ì»´í¬ë„ŒíŠ¸ ì°¾ê¸° ë° ì—…ë°ì´íŠ¸
        try {
            if (txt_company_count && txt_company_count.setLabel) {
                txt_company_count.setLabel(`ì°¸ì—¬ ê¸°ì—…: ${totalCount}ê°œ`);
            } else {
                console.warn('txt_company_count ì»´í¬ë„ŒíŠ¸ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
            }
        } catch (error) {
            console.error('íšŒì‚¬ ìˆ˜ ì—…ë°ì´íŠ¸ ì˜¤ë¥˜:', error);
        }

        // ë™ì ìœ¼ë¡œ íšŒì‚¬ ëª©ë¡ ìƒì„± (ì•½ê°„ì˜ ì§€ì—° í›„ ì‹¤í–‰)
        setTimeout(function() {
            scwin.createCompanyList();
        }, 200);
};

