scwin.createCompanyList = function () {
    console.log('íšŒì‚¬ ëª©ë¡ ìƒì„± ì‹œì‘');

    // WebSquare ì»´í¬ë„ŒíŠ¸ ê°ì²´ ê°€ì ¸ì˜¤ê¸°
    var containerComponent = grp_companies_container;
    if (!containerComponent) {
        console.error('íšŒì‚¬ ëª©ë¡ ì»¨í…Œì´ë„ˆ ì»´í¬ë„ŒíŠ¸ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
        return;
    }

    // setTimeoutì„ ì‚¬ìš©í•´ì„œ ë Œë”ë§ ì™„ë£Œ í›„ ì‹¤í–‰
    setTimeout(function() {
        try {
            // ë‹¤ì–‘í•œ ë°©ë²•ìœ¼ë¡œ DOM ìš”ì†Œ ì°¾ê¸° ì‹œë„
            var container = null;

            // ë°©ë²• 1: ì»´í¬ë„ŒíŠ¸ì˜ ì‹¤ì œ DOM ìš”ì†Œ ì°¾ê¸°
            if (containerComponent.getRef && typeof containerComponent.getRef === 'function') {
                container = containerComponent.getRef();
            }

            // ë°©ë²• 2: IDë¡œ ì§ì ‘ ì°¾ê¸°
            if (!container) {
                container = document.getElementById('grp_companies_container');
            }

            // ë°©ë²• 3: í´ë˜ìŠ¤ëª…ìœ¼ë¡œ ì°¾ê¸°
            if (!container) {
                var elements = document.getElementsByClassName('companies-container');
                if (elements.length > 0) {
                    container = elements[0];
                }
            }

            // ë°©ë²• 4: querySelector ì‚¬ìš©
            if (!container) {
                container = document.querySelector('.companies-container');
            }

            if (!container) {
                console.error('íšŒì‚¬ ëª©ë¡ ì»¨í…Œì´ë„ˆ DOM ìš”ì†Œë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
                // ëŒ€ì•ˆ: ì»´í¬ë„ŒíŠ¸ ë‚´ë¶€ì— ì§ì ‘ HTML ì‚½ì…
                if (containerComponent.setHTML && typeof containerComponent.setHTML === 'function') {
                    var htmlContent = scwin.generateCompanyListHTML();
                    containerComponent.setHTML(htmlContent);
                }
                return;
            }

            console.log('ì»¨í…Œì´ë„ˆ DOM ìš”ì†Œë¥¼ ì°¾ì•˜ìŠµë‹ˆë‹¤:', container);

            // ê¸°ì¡´ ë‚´ìš© ì œê±°
            container.innerHTML = '';

            // DataListì—ì„œ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
            var rowCount = dlt_company_list.getRowCount();
            console.log('íšŒì‚¬ ë°ì´í„° í–‰ ìˆ˜:', rowCount);

            if (rowCount === 0) {
                container.innerHTML = '<div style="text-align: center; padding: 40px; color: #666; font-size: 16px;">ë“±ë¡ëœ ê¸°ì—…ì´ ì—†ìŠµë‹ˆë‹¤.</div>';
                return;
            }

            // íšŒì‚¬ ë°ì´í„°ë¥¼ ê·¸ë£¹í™”í•˜ì—¬ í–‰ë³„ë¡œ í‘œì‹œ (í•œ í–‰ì— 4ê°œì”©)
            var companiesPerRow = 4;
            var currentRow = null;

            for (var i = 0; i < rowCount; i++) {
                // ìƒˆë¡œìš´ í–‰ ìƒì„± (4ê°œë§ˆë‹¤)
                if (i % companiesPerRow === 0) {
                    currentRow = document.createElement('div');
                    currentRow.className = 'company-row';
                    container.appendChild(currentRow);
                }

                // íšŒì‚¬ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
                var companyData = {
                    userId: dlt_company_list.getCellData(i, 'userId'),
                    name: dlt_company_list.getCellData(i, 'name') || 'íšŒì‚¬ëª… ì—†ìŒ',
                    logoFileName: dlt_company_list.getCellData(i, 'logoFileName')
                };

                console.log('íšŒì‚¬ ë°ì´í„° ' + (i+1) + ':', companyData);

                // íšŒì‚¬ ì¹´ë“œ ìš”ì†Œ ìƒì„±
                var companyCard = document.createElement('div');
                companyCard.className = 'company-item';
                companyCard.style.cursor = 'pointer';

                // ë¡œê³ ê°€ ìˆëŠ” ê²½ìš°ì™€ ì—†ëŠ” ê²½ìš° ë¶„ê¸°
                var cardContent = '';
                if (companyData.logoFileName && companyData.logoFileName.trim() !== '') {
                    // ë¡œê³ ê°€ ìˆëŠ” ê²½ìš°
                    cardContent = '<div style="display: flex; align-items: center; justify-content: center; height: 100%; gap: 10px;">' +
                                 '<img src="/InsWebApp/upload/logo/' + companyData.logoFileName + '" ' +
                                 'style="max-width: 40px; max-height: 40px; object-fit: contain;" ' +
                                 'onerror="this.style.display=\'none\'; this.nextElementSibling.style.display=\'block\';" alt="' + companyData.name + ' ë¡œê³ " />' +
                                 '<span class="company-name" style="display: none;">' + companyData.name + '</span>' +
                                 '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>' +
                                 '</div>';
                } else {
                    // ë¡œê³ ê°€ ì—†ëŠ” ê²½ìš°
                    cardContent = '<div style="display: flex; align-items: center; justify-content: center; height: 100%;">' +
                                 '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>' +
                                 '</div>';
                }

                companyCard.innerHTML = cardContent;

                // í´ë¦­ ì´ë²¤íŠ¸ ì¶”ê°€ (í´ë¡œì € ì‚¬ìš©í•˜ì—¬ ë°ì´í„° ë°”ì¸ë”©)
                (function(data) {
                    companyCard.addEventListener('click', function() {
                        scwin.onCompanyCardClick(data);
                    });
                })(companyData);

                // í˜¸ë²„ íš¨ê³¼ ì¶”ê°€
                companyCard.addEventListener('mouseenter', function() {
                    this.style.backgroundColor = '#f8fbff';
                    this.style.borderColor = '#007bff';
                });

                companyCard.addEventListener('mouseleave', function() {
                    this.style.backgroundColor = '#fff';
                    this.style.borderColor = '#e8f0fe';
                });

                // í˜„ì¬ í–‰ì— ì¹´ë“œ ì¶”ê°€
                currentRow.appendChild(companyCard);
            }

            console.log('íšŒì‚¬ ëª©ë¡ì´ ì„±ê³µì ìœ¼ë¡œ ìƒì„±ë˜ì—ˆìŠµë‹ˆë‹¤. ì´ ' + rowCount + 'ê°œ ê¸°ì—…');

        } catch (error) {
            console.error('íšŒì‚¬ ëª©ë¡ ìƒì„± ì¤‘ ì˜¤ë¥˜ ë°œìƒ:', error);
        }
    }, 100); // 100ms í›„ ì‹¤í–‰
};

// HTML ìƒì„± í—¬í¼ í•¨ìˆ˜
scwin.generateCompanyListHTML = function() {
    var rowCount = dlt_company_list.getRowCount();
    if (rowCount === 0) {
        return '<div style="text-align: center; padding: 40px; color: #666; font-size: 16px;">ë“±ë¡ëœ ê¸°ì—…ì´ ì—†ìŠµë‹ˆë‹¤.</div>';
    }

    var html = '';
    var companiesPerRow = 4;

    for (var i = 0; i < rowCount; i++) {
        if (i % companiesPerRow === 0) {
            if (i > 0) {
                html += '</div>'; // ì´ì „ í–‰ ë‹«ê¸°
            }
            html += '<div class="company-row">';
        }

        var companyData = {
            userId: dlt_company_list.getCellData(i, 'userId'),
            name: dlt_company_list.getCellData(i, 'name') || 'íšŒì‚¬ëª… ì—†ìŒ',
            logoFileName: dlt_company_list.getCellData(i, 'logoFileName')
        };

        html += '<div class="company-item" style="cursor: pointer;" onclick="scwin.onCompanyCardClick({userId:' + companyData.userId + ',name:\'' + companyData.name + '\',logoFileName:\'' + (companyData.logoFileName || '') + '\'})">';

        if (companyData.logoFileName && companyData.logoFileName.trim() !== '') {
            html += '<div style="display: flex; align-items: center; justify-content: center; height: 100%; gap: 10px;">';
            html += '<img src="/InsWebApp/upload/logo/' + companyData.logoFileName + '" style="max-width: 40px; max-height: 40px; object-fit: contain;" alt="' + companyData.name + ' ë¡œê³ " />';
            html += '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>';
            html += '</div>';
        } else {
            html += '<div style="display: flex; align-items: center; justify-content: center; height: 100%;">';
            html += '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>';
            html += '</div>';
        }

        html += '</div>';
    }

    if (rowCount > 0) {
        html += '</div>'; // ë§ˆì§€ë§‰ í–‰ ë‹«ê¸°
    }

    return html;
};

/* íšŒì‚¬ ì¹´ë“œ í´ë¦­ ì´ë²¤íŠ¸ í•¸ë“¤ëŸ¬ */
scwin.onCompanyCardClick = function(companyData) {
    // íšŒì‚¬ ìƒì„¸ ì •ë³´ íŒì—… ë˜ëŠ” í˜ì´ì§€ ì´ë™
    console.log("ì„ íƒëœ íšŒì‚¬:", companyData);

    $c.win.alert(companyData.name + " ê¸°ì—… ì •ë³´ í˜ì´ì§€ë¡œ ì´ë™í•©ë‹ˆë‹¤.\n(userId: " + companyData.userId + ")");
};

