// Í≤ÄÏÉâ Ïã§Ìñâ
scwin.performSearch = function () {
    var searchInput = document.getElementById('searchInput');
    if (!searchInput) {
        alert("Í≤ÄÏÉâÏ∞ΩÏùÑ Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.");
        return;
    }

    var searchText = searchInput.value;
    if (searchText && searchText.trim() !== "") {
        alert("'" + searchText + "'Î•º Í≤ÄÏÉâÌï©ÎãàÎã§. (ÏïÑÏßÅ Í≤ÄÏÉâ Í∏∞Îä•ÏùÄ Íµ¨ÌòÑÎêòÏßÄ ÏïäÏïòÏäµÎãàÎã§)");
        console.log("Í≤ÄÏÉâÏñ¥:", searchText);
    } else {
        alert("Í≤ÄÏÉâÏñ¥Î•º ÏûÖÎ†•Ìï¥Ï£ºÏÑ∏Ïöî.");
    }
};

// Í∏∞Ï°¥ Ìï®ÏàòÎì§Í≥ºÏùò Ìò∏ÌôòÏÑ±ÏùÑ ÏúÑÌï¥ Ïú†ÏßÄ
