/**
* Î∞òÏùëÌòï Í∞ÄÏö¥Îç∞ Ï†ïÎ†¨ ÏúÑÏπò Î∞è ÌÅ¨Í∏∞ Í≥ÑÏÇ∞ Î©îÏÑúÎìú
* @param {Object} options - ÏÑ§Ï†ï ÏòµÏÖò
* @param {number} options.maxWidth - ÏµúÎåÄ ÎÑàÎπÑ (Í∏∞Î≥∏Í∞í: 800)
* @param {number} options.minWidth - ÏµúÏÜå ÎÑàÎπÑ (Í∏∞Î≥∏Í∞í: 400)
* @param {number} options.maxHeight - ÏµúÎåÄ ÎÜíÏù¥ (Í∏∞Î≥∏Í∞í: 600)
* @param {number} options.minHeight - ÏµúÏÜå ÎÜíÏù¥ (Í∏∞Î≥∏Í∞í: 300)
* @param {number} options.widthRatio - ÌôîÎ©¥ ÎåÄÎπÑ ÎÑàÎπÑ ÎπÑÏú® (Í∏∞Î≥∏Í∞í: 0.8)
* @param {number} options.heightRatio - ÌôîÎ©¥ ÎåÄÎπÑ ÎÜíÏù¥ ÎπÑÏú® (Í∏∞Î≥∏Í∞í: 0.8)
* @returns {Object} {width, height, left, top} Í∞íÏùÑ Ìè¨Ìï®Ìïú Í∞ùÏ≤¥
*/
scwin.getResponsiveCenterPosition = function (options) {
    options = options || {};

    var browserWidth = window.innerWidth || document.documentElement.clientWidth;
    var browserHeight = window.innerHeight || document.documentElement.clientHeight;

    // ÌôîÎ©¥ ÌÅ¨Í∏∞Ïùò ÎπÑÏú®Î°ú ÌåùÏóÖ ÌÅ¨Í∏∞ ÏÑ§Ï†ï (ÏµúÎåÄ/ÏµúÏÜå ÌÅ¨Í∏∞ Ï†úÌïú)
    var popupWidth = Math.min(
        options.maxWidth || 800,
        Math.max(options.minWidth || 400, browserWidth * (options.widthRatio || 0.8))
    );
    var popupHeight = Math.min(
        options.maxHeight || 600,
        Math.max(options.minHeight || 300, browserHeight * (options.heightRatio || 0.8))
    );

    // Í∞ÄÏö¥Îç∞ ÏúÑÏπò Í≥ÑÏÇ∞
    var leftPos = (browserWidth - popupWidth) / 2;
    var topPos = (browserHeight - popupHeight) / 2;

    return {
        width: popupWidth,
        height: popupHeight,
        left: leftPos,
        top: topPos
    };
};

/**
 * ÌöåÏÇ¨ Î™©Î°ù Ï°∞Ìöå
 */
scwin.loadCompanyList = function () {
    // role_id=2 (COMPANY) ÏÇ¨Ïö©ÏûêÎì§ Ï°∞Ìöå
    dmp_corporateSearchVo.set("roleId", 2);
    dmp_corporateSearchVo.set("pageIndex", 1);
    dmp_corporateSearchVo.set("pageSize", 12);

    // ÏÑúÎ≤Ñ ÌÜµÏã† Ïã§Ìñâ
    $c.sbm.execute($p, sbm_selectCompanyList);
};

/**
 * ÌöåÏÇ¨ Î™©Î°ù Ï°∞Ìöå ÏÑ±Í≥µ ÏΩúÎ∞±
 */
scwin.sbm_selectCompanyList_submitdone = function (e) {
    // ÏùëÎãµ Îç∞Ïù¥ÌÑ∞ ÌôïÏù∏
    var elData = e.responseJSON.elData;
    var elHeader = e.responseJSON.elHeader;

    if (elHeader && elHeader.resSuc === false) {
        $c.win.alert(`ÏóêÎü¨ÏΩîÎìú: ${elHeader.resCode}\nÏóêÎü¨Î©îÏãúÏßÄ: ${elHeader.resMsg}`);
        return false;
    }

    // Ï¥ù ÌöåÏÇ¨ Ïàò ÏóÖÎç∞Ïù¥Ìä∏
    var totalCount = dlt_company_list.getRowCount();
    console.log('Îç∞Ïù¥ÌÑ∞ Î°úÎìú ÏôÑÎ£å. Ï¥ù ÌöåÏÇ¨ Ïàò:', totalCount);

    // txt_company_count Ïª¥Ìè¨ÎÑåÌä∏ Ï∞æÍ∏∞ Î∞è ÏóÖÎç∞Ïù¥Ìä∏
    try {
        if (txt_company_count && txt_company_count.setLabel) {
            txt_company_count.setLabel(`Ï∞∏Ïó¨ Í∏∞ÏóÖ: ${totalCount}Í∞ú`);
        } else {
            console.warn('txt_company_count Ïª¥Ìè¨ÎÑåÌä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
        }
    } catch (error) {
        console.error('ÌöåÏÇ¨ Ïàò ÏóÖÎç∞Ïù¥Ìä∏ Ïò§Î•ò:', error);
    }

    // ÎèôÏ†ÅÏúºÎ°ú ÌöåÏÇ¨ Î™©Î°ù ÏÉùÏÑ± (ÏïΩÍ∞ÑÏùò ÏßÄÏó∞ ÌõÑ Ïã§Ìñâ)
    setTimeout(function () {
        scwin.createCompanyList();
    }, 200);
};

/**
 * ÌöåÏÇ¨ Î™©Î°ù Ï°∞Ìöå ÏóêÎü¨ ÏΩúÎ∞±
 */
scwin.sbm_selectCompanyList_submiterror = function (e) {
    console.error("ÌöåÏÇ¨ Î™©Î°ù Ï°∞Ìöå Ïã§Ìå®:", e);
    $c.win.alert("ÌöåÏÇ¨ Î™©Î°ùÏùÑ Î∂àÎü¨Ïò§ÎäîÎç∞ Ïã§Ìå®ÌñàÏäµÎãàÎã§.");
};

scwin.createCompanyList = function () {
    // WebSquare Ïª¥Ìè¨ÎÑåÌä∏ Í∞ùÏ≤¥ Í∞ÄÏ†∏Ïò§Í∏∞
    var containerComponent = grp_companies_container;
    if (!containerComponent) {
        console.error('ÌöåÏÇ¨ Î™©Î°ù Ïª®ÌÖåÏù¥ÎÑà Ïª¥Ìè¨ÎÑåÌä∏Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.');
        return;
    }

    // setTimeoutÏùÑ ÏÇ¨Ïö©Ìï¥ÏÑú Î†åÎçîÎßÅ ÏôÑÎ£å ÌõÑ Ïã§Ìñâ
    setTimeout(function () {
        try {
            var container = document.querySelector('.companies-container');

            // Í∏∞Ï°¥ ÎÇ¥Ïö© Ï†úÍ±∞
            container.innerHTML = '';

            // DataListÏóêÏÑú Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
            var rowCount = dlt_company_list.getRowCount();

            if (rowCount === 0) {
                container.innerHTML = '<div style="text-align: center; padding: 40px; color: #666; font-size: 16px;">Îì±Î°ùÎêú Í∏∞ÏóÖÏù¥ ÏóÜÏäµÎãàÎã§.</div>';
                return;
            }

            // ÌöåÏÇ¨ Îç∞Ïù¥ÌÑ∞Î•º Í∑∏Î£πÌôîÌïòÏó¨ ÌñâÎ≥ÑÎ°ú ÌëúÏãú (Ìïú ÌñâÏóê 4Í∞úÏî©)
            var companiesPerRow = 4;
            var currentRow = null;

            for (var i = 0; i < rowCount; i++) {
                // ÏÉàÎ°úÏö¥ Ìñâ ÏÉùÏÑ± (4Í∞úÎßàÎã§)
                if (i % companiesPerRow === 0) {
                    currentRow = document.createElement('div');
                    currentRow.className = 'company-row';
                    container.appendChild(currentRow);
                }

                // ÌöåÏÇ¨ Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
                var companyData = {
                    userId: dlt_company_list.getCellData(i, 'userId'),
                    name: dlt_company_list.getCellData(i, 'name') || 'ÌöåÏÇ¨Î™Ö ÏóÜÏùå',
                    logoFileName: dlt_company_list.getCellData(i, 'logoFileName')
                };

                // ÌöåÏÇ¨ Ïπ¥Îìú ÏöîÏÜå ÏÉùÏÑ±
                var companyCard = document.createElement('div');
                companyCard.className = 'company-item';
                companyCard.style.cursor = 'pointer';

                // Î°úÍ≥†Í∞Ä ÏûàÎäî Í≤ΩÏö∞ÏôÄ ÏóÜÎäî Í≤ΩÏö∞ Î∂ÑÍ∏∞
                var cardContent = '';
                if (companyData.logoFileName && companyData.logoFileName.trim() !== '') {
                    cardContent = '<div style="display: flex; align-items: center; justify-content: center; height: 100%; gap: 10px;">' +
                                 '<img src="/InsWebApp/images/ws5/tutorial_image.png" ' +
                                 'style="max-width: 100%; max-height: 100%; width: auto; height: auto; object-fit: contain;" ' +
                                 'onerror="this.style.display=\'none\'; this.nextElementSibling.style.display=\'block\';" alt="' + companyData.name + ' Î°úÍ≥†" />' +
                                 '<span class="company-name" style="display: none;">' + companyData.name + '</span>' +
                                 '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>' +
                                 '</div>';
                } else {
                    cardContent = '<div style="display: flex; align-items: center; justify-content: center; height: 100%; gap: 10px;">' +
                                 '<img src="/InsWebApp/images/ws5/logo_inswave_w.png" style="width: 40px; height: 40px; object-fit: contain;" alt="Í∏∞Î≥∏ Î°úÍ≥†" />' +
                                 '<span class="company-name" style="font-weight: 500;">' + companyData.name + '</span>' +
                                 '</div>';
                }

                companyCard.innerHTML = cardContent;

                // ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ Ï∂îÍ∞Ä (ÌÅ¥Î°úÏ†Ä ÏÇ¨Ïö©ÌïòÏó¨ Îç∞Ïù¥ÌÑ∞ Î∞îÏù∏Îî©)
                (function (data) {
                    companyCard.addEventListener('click', function () {
                        scwin.onCompanyCardClick(data);
                    });
                })(companyData);

                // Ìò∏Î≤Ñ Ìö®Í≥º Ï∂îÍ∞Ä
                companyCard.addEventListener('mouseenter', function () {
                    this.style.backgroundColor = '#f8fbff';
                    this.style.borderColor = '#007bff';
                });

                companyCard.addEventListener('mouseleave', function () {
                    this.style.backgroundColor = '#fff';
                    this.style.borderColor = '#e8f0fe';
                });

                // ÌòÑÏû¨ ÌñâÏóê Ïπ¥Îìú Ï∂îÍ∞Ä
                currentRow.appendChild(companyCard);
            }

        } catch (error) {
            console.error('ÌöåÏÇ¨ Î™©Î°ù ÏÉùÏÑ± Ï§ë Ïò§Î•ò Î∞úÏÉù:', error);
        }
    }, 100); // 100ms ÌõÑ Ïã§Ìñâ
};


