scwin.onpageload = function () {

    var userInfoStr = WebSquare.cookie.getCookie("userInfo");
    if (!userInfoStr) {
        $p.url("/InsWebApp/ui/common/MainPage.xml");
        return;
    }

    try {
        var userInfo = JSON.parse(userInfoStr);
        if (userInfo.role !== "COMPANY") {
            $p.url("/InsWebApp/ui/common/MainPage.xml");
            return;
        }

        console.log("ê¸°ì—… ì •ë³´:", userInfo);

        // í˜ì´ì§€ ë¡œë“œ ì‹œ ì´ˆê¸°í™”
        scwin.initializeDashboard();

        // ì• ë‹ˆë©”ì´ì…˜ íš¨ê³¼ ì ìš©
        scwin.applyAnimations();

    } catch (e) {
        console.error("ì‚¬ìš©ì ì •ë³´ íŒŒì‹± ì˜¤ë¥˜:", e);
        $p.url("/InsWebApp/ui/common/MainPage.xml");
    }
};

// ëŒ€ì‹œë³´ë“œ ì´ˆê¸°í™”
scwin.initializeDashboard = function () {
    console.log("ëŒ€ì‹œë³´ë“œ ì´ˆê¸°í™” ì‹œì‘");

    // íšŒì‚¬ ID ì„¤ì •
    var userInfoStr = WebSquare.cookie.getCookie("userInfo");
    var userInfo = JSON.parse(userInfoStr);
    var companyId = userInfo.companyId;

    if (companyId) {
        dmp_postVo.set("companyId", companyId);

        // ê³µê³  ëª©ë¡ ì¡°íšŒ (í†µê³„ëŠ” ì´ ê²°ê³¼ì—ì„œ ê³„ì‚°)
        scwin.loadPostList();
    } else {
        console.error("íšŒì‚¬ IDë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
        $c.win.alert("íšŒì‚¬ ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ë‹¤ì‹œ ë¡œê·¸ì¸í•´ì£¼ì„¸ìš”.");
    }
};

// ê³µê³  ë°ì´í„°ë¡œë¶€í„° í†µê³„ ê³„ì‚°
scwin.calculateStatistics = function (postList) {
    console.log("í†µê³„ ê³„ì‚° ì‹œì‘");

    var totalJobs = postList.length;
    var activeJobs = 0;
    var closedJobs = 0;
    var currentDate = new Date();

    console.log("í˜„ì¬ ì‹œê°„:", currentDate);

    // ê° ê³µê³ ì˜ ë§Œë£Œì¼ì„ í™•ì¸í•´ì„œ ìƒíƒœ ê³„ì‚°
    for (var i = 0; i < postList.length; i++) {
        var post = postList[i];
        var expiresAt = post.expiresAt;

        // ë§Œë£Œì¼ íŒŒì‹±
        var expirationDate = null;
        if (expiresAt) {
            // ISO 8601 í˜•ì‹ì˜ ë‚ ì§œ ì²˜ë¦¬ (ì˜ˆ: 2025-07-21T00:21:37.000Z)
            expirationDate = new Date(expiresAt);
        }

        // ë§Œë£Œ ì—¬ë¶€ í™•ì¸
        var isExpired = expirationDate && expirationDate < currentDate;

        console.log("ê³µê³  ID:", post.jobPostingId,
                   "ì œëª©:", post.title,
                   "ë§Œë£Œì¼:", expiresAt,
                   "íŒŒì‹±ëœ ë‚ ì§œ:", expirationDate,
                   "ë§Œë£Œë¨:", isExpired);

        if (isExpired) {
            closedJobs++;
        } else {
            activeJobs++;
        }
    }

    // ì¹´ìš´í„° ì• ë‹ˆë©”ì´ì…˜ê³¼ í•¨ê»˜ ê°’ ì„¤ì •
    scwin.animateCounter(tbx_totalJobs, totalJobs);
    scwin.animateCounter(tbx_activeJobs, activeJobs);
    scwin.animateCounter(tbx_closedJobs, closedJobs);

    console.log("í†µê³„ ì—…ë°ì´íŠ¸ ì™„ë£Œ:", {
        totalJobs: totalJobs,
        activeJobs: activeJobs,
        closedJobs: closedJobs,
        currentDate: currentDate
    });
};

// ì¹´ìš´í„° ì• ë‹ˆë©”ì´ì…˜
scwin.animateCounter = function (element, targetValue) {
    var currentValue = 0;
    var increment = targetValue / 20;
    var timer = setInterval(function() {
        currentValue += increment;
        if (currentValue >= targetValue) {
            currentValue = targetValue;
            clearInterval(timer);
        }
        element.setValue(Math.floor(currentValue).toString());
    }, 50); // 50ms ê°„ê²©
};

// ê³µê³  ëª©ë¡ ì¡°íšŒ
scwin.loadPostList = function () {
    console.log("ê³µê³  ëª©ë¡ ì¡°íšŒ ì‹œì‘");

    // í˜ì´ì§€ ë²ˆí˜¸ ì´ˆê¸°í™”
    dmp_postVo.set("pageIndex", "1");
    if (pgl_postList) {
        pgl_postList.setSelectedIndex(1);
    }

    // ì„œë²„í†µì‹  - ê³µê³ ì •ë³´ ì¡°íšŒ
    $c.sbm.execute($p, sbm_selectPostVoList);
};

// ê³µê³  ëª©ë¡ ì¡°íšŒ ì™„ë£Œ
scwin.sbm_postList_submitdone = function (e) {
    console.log("ê³µê³  ëª©ë¡ ì¡°íšŒ ì™„ë£Œ:", e.responseJSON);

    var elHeader = e.responseJSON.elHeader;
    if (elHeader && elHeader.resSuc === false) {
        console.error("ê³µê³  ëª©ë¡ ì¡°íšŒ ì—ëŸ¬:", elHeader);
        $c.win.alert(`ì—ëŸ¬: ${elHeader.resMsg}`);
        return false;
    }

    var elData = e.responseJSON.elData;
    var originalPostList = elData.postVoList || [];

    // í™œì„± ê³µê³ ë§Œ í•„í„°ë§ (ë§Œë£Œë˜ì§€ ì•Šì€ ê³µê³ ë§Œ)
    var currentDate = new Date();
    var activePostList = [];

    for (var i = 0; i < originalPostList.length; i++) {
        var post = originalPostList[i];
        var expirationDate = post.expiresAt ? new Date(post.expiresAt) : null;
        var isExpired = expirationDate && expirationDate < currentDate;

        if (!isExpired) {
            activePostList.push(post);
        }
    }

    console.log("ì „ì²´ ê³µê³ :", originalPostList.length, "í™œì„± ê³µê³ :", activePostList.length);

    // ì„ íƒëœ ê³µê³  ì •ë³´ ì´ˆê¸°í™”
    dmp_postVoDetail.setEmptyValue();

    // ì´ ë ˆì½”ë“œ ìˆ˜ ì—…ë°ì´íŠ¸ (í™œì„± ê³µê³ ë§Œ)
    if (spn_listCnt) {
        spn_listCnt.setLabel(activePostList.length);
    }

    // ë°ì´í„°ë¦¬ìŠ¤íŠ¸ì— í™œì„± ê³µê³  ë°”ì¸ë”© (í˜ì´ì§•ì„ ìœ„í•´ ìœ ì§€)
    dlt_postVoList.setJSON(activePostList);

    // ì¹´ë“œ ë ˆì´ì•„ì›ƒìœ¼ë¡œ ê³µê³  ëª©ë¡ ë Œë”ë§
    scwin.renderPostCards(activePostList);

    // í˜ì´ì§€ ë„¤ë¹„ê²Œì´í„° ì—…ë°ì´íŠ¸ (í™œì„± ê³µê³  ê¸°ì¤€)
    if (pgl_postList && activePostList.length > 0) {
        var pageSize = dmp_postVo.get("pageSize") || 9; // ì¹´ë“œëŠ” 9ê°œì”© í‘œì‹œ
        var totalPageCount = Math.ceil(activePostList.length / pageSize);
        pgl_postList.setCount(totalPageCount);
    }

    // í†µê³„ ê³„ì‚° (ì „ì²´ ê³µê³  ë¦¬ìŠ¤íŠ¸ë¡œ, ë‚´ë¶€ì—ì„œ ë§Œë£Œ ì—¬ë¶€ í™•ì¸)
    if (originalPostList && originalPostList.length > 0) {
        scwin.calculateStatistics(originalPostList);
    } else {
        // ë°ì´í„°ê°€ ì—†ìœ¼ë©´ 0ìœ¼ë¡œ ì„¤ì •
        scwin.animateCounter(tbx_totalJobs, 0);
        scwin.animateCounter(tbx_activeJobs, 0);
        scwin.animateCounter(tbx_closedJobs, 0);
    }

    console.log("ê³µê³  ëª©ë¡ ì¹´ë“œ ë Œë”ë§ ì™„ë£Œ - í™œì„± ê³µê³ ë§Œ í‘œì‹œ");
};

// ì¹´ë“œ ë ˆì´ì•„ì›ƒìœ¼ë¡œ ê³µê³  ëª©ë¡ ë Œë”ë§ (PostList.xml ë°©ì‹ ì ìš©)
scwin.renderPostCards = function (postList) {
    console.log("=== ê³µê³  ì¹´ë“œ ë Œë”ë§ ì‹œì‘ ===");
    console.log("postList:", postList);
    console.log("postList.length:", postList ? postList.length : "null/undefined");

    try {
        // PostList.xml ë°©ì‹: document.querySelector ì‚¬ìš©
        var postsGridContainer = document.querySelector('.posts_grid');
        if (!postsGridContainer) {
            console.error("posts_grid ì»¨í…Œì´ë„ˆë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
            if (debug_message) {
                debug_message.show();
                debug_message.setLabel("ì»¨í…Œì´ë„ˆ ì°¾ê¸° ì‹¤íŒ¨");
            }
            return;
        }

        console.log("posts_grid ì»¨í…Œì´ë„ˆ ì°¾ìŒ:", postsGridContainer);

        // ê¸°ì¡´ ì¹´ë“œë“¤ ì œê±°
        postsGridContainer.innerHTML = '';

        var noPosts = grp_noPosts;
        var pagination = grp_pagination;

        if (!postList || postList.length === 0) {
            console.log("ê³µê³ ê°€ ì—†ìŒ - ë¹ˆ ìƒíƒœ í‘œì‹œ");
            if (noPosts) noPosts.show();
            if (pagination) pagination.hide();
            if (debug_message) debug_message.hide();
            return;
        }

        if (noPosts) noPosts.hide();
        if (pagination) pagination.show();

        // í˜ì´ì§• ì²˜ë¦¬
        var pageIndex = parseInt(dmp_postVo.get("pageIndex") || "1");
        var pageSize = parseInt(dmp_postVo.get("pageSize") || "9");
        var startIndex = (pageIndex - 1) * pageSize;
        var endIndex = Math.min(startIndex + pageSize, postList.length);

        console.log("í˜ì´ì§• ì •ë³´:", {
            pageIndex: pageIndex, 
            pageSize: pageSize, 
            startIndex: startIndex, 
            endIndex: endIndex
        });

        if (debug_message) {
            debug_message.show();
            debug_message.setLabel("ì¹´ë“œ ìƒì„± ì¤‘... (" + (endIndex - startIndex) + "ê°œ)");
        }

        // í˜„ì¬ í˜ì´ì§€ì˜ ê³µê³ ë“¤ë§Œ ì¹´ë“œë¡œ ìƒì„±
        for (var i = startIndex; i < endIndex; i++) {
            var post = postList[i];
            console.log("ì¹´ë“œ ìƒì„± ì‹œë„:", i, post ? post.title : "no title");
            var cardElement = scwin.createPostCard(post, i);
            if (cardElement) {
                postsGridContainer.appendChild(cardElement);
            }
        }

        if (debug_message) debug_message.hide();

        console.log("=== ì¹´ë“œ ë Œë”ë§ ì™„ë£Œ ===");

    } catch (e) {
        console.error("ë Œë”ë§ ì „ì²´ ì˜¤ë¥˜:", e);
        console.error("ì˜¤ë¥˜ ìŠ¤íƒ:", e.stack);
        if (debug_message) {
            debug_message.show();
            debug_message.setLabel("ë Œë”ë§ ì˜¤ë¥˜: " + e.message);
        }
    }
};

// PostList.xml ë°©ì‹ì˜ ì¹´ë“œ ì œê±° (ë¶ˆí•„ìš”í•œ í•¨ìˆ˜ë“¤ ì •ë¦¬)

// ê°œë³„ ê³µê³  ì¹´ë“œ ìƒì„± (PostList.xml ë°©ì‹ ì ìš©)
scwin.createPostCard = function (post, index) {
    console.log("=== ê°œë³„ ì¹´ë“œ ìƒì„± ì‹œì‘ ===", index);
    try {
        if (!post) {
            console.warn("ê³µê³  ë°ì´í„°ê°€ ì—†ìŠµë‹ˆë‹¤:", index);
            return null;
        }

        console.log("ê³µê³  ë°ì´í„°:", post.title);

        // PostList.xml ë°©ì‹: document.createElement('xf:group') ì‚¬ìš©
        var card = document.createElement('xf:group');
        card.className = 'post_card';
        card.setAttribute('data-job-id', post.jobPostingId || '');
        card.setAttribute('data-index', index);
        card.onclick = function() {
            console.log("ì¹´ë“œ í´ë¦­ë¨:", post.title);
            scwin.onPostCardClick(post);
        };

        // ì¹´ë“œ ë°ì´í„° ì¤€ë¹„
        var title = post.title || "ì œëª© ì—†ìŒ";
        var description = post.description || "ì„¤ëª… ì—†ìŒ";
        var location = post.location || "ìœ„ì¹˜ ë¯¸ì •";
        var workType = post.workType || "ë¯¸ì •";
        var experienceLevel = post.experienceLevel || "ê²½ë ¥ë¬´ê´€";
        var salaryRange = post.salaryRange || "í˜‘ì˜";
        var postedAt = scwin.formatDate(post.postedAt);
        var jobPostingId = post.jobPostingId || "";

        // ì„¤ëª… í…ìŠ¤íŠ¸ ê¸¸ì´ ì œí•œ
        if (description && description.length > 80) {
            description = description.substring(0, 80) + "...";
        }

        // PostList.xml ë°©ì‹ì˜ HTML êµ¬ì¡° (xf:group íƒœê·¸ ì‚¬ìš©)
        card.innerHTML = 
            '<xf:group class="post_header">' +
                '<xf:group class="post_title">' + scwin.escapeHtml(title) + '</xf:group>' +
                '<xf:group class="post_status">í™œì„±</xf:group>' +
            '</xf:group>' +
            '<xf:group class="post_description">' + scwin.escapeHtml(description) + '</xf:group>' +
            '<xf:group class="post_details">' +
                '<xf:group class="post_detail_item">' +
                    '<xf:group class="post_detail_icon">&#128205;</xf:group>' +
                    '<xf:group>' + scwin.escapeHtml(location) + '</xf:group>' +
                '</xf:group>' +
                '<xf:group class="post_detail_item">' +
                    '<xf:group class="post_detail_icon">&#127968;</xf:group>' +
                    '<xf:group>' + scwin.escapeHtml(workType) + '</xf:group>' +
                '</xf:group>' +
                '<xf:group class="post_detail_item">' +
                    '<xf:group class="post_detail_icon">&#128188;</xf:group>' +
                    '<xf:group>' + scwin.escapeHtml(experienceLevel) + '</xf:group>' +
                '</xf:group>' +
                '<xf:group class="post_detail_item">' +
                    '<xf:group class="post_detail_icon">&#128176;</xf:group>' +
                    '<xf:group>' + scwin.escapeHtml(salaryRange) + '</xf:group>' +
                '</xf:group>' +
            '</xf:group>' +
            '<xf:group class="post_footer">' +
                '<xf:group class="post_date">' +
                    '<xf:group>&#128197;</xf:group>' +
                    scwin.escapeHtml(postedAt) +
                '</xf:group>' +
                '<xf:group class="post_id">ID: ' + scwin.escapeHtml(jobPostingId) + '</xf:group>' +
            '</xf:group>';

        console.log("ì¹´ë“œ ìƒì„± ì™„ë£Œ:", title);
        return card;

    } catch (e) {
        console.error("ì¹´ë“œ ìƒì„± ì˜¤ë¥˜:", e);
        console.error("ì˜¤ë¥˜ ìŠ¤íƒ:", e.stack);
        return null;
    }
};

