scwin.loadPostList = function() {
	console.log("=== ê³µê³  ëª©ë¡ ì¡°íšŒ ì‹œìž‘ ===");

	// íŽ˜ì´ì§€ ë²ˆí˜¸ ì´ˆê¸°í™”
	dmp_postVo.set("pageIndex", "1");
	pgl_postList.setSelectedIndex(1);

	// ë¡œê·¸ì¸ëœ ì‚¬ìš©ìžì˜ companyIdë¥¼ ê°€ì ¸ì™€ ì„¤ì •í•©ë‹ˆë‹¤.
	var loggedInCompanyId = ""; 
	var userInfoCookie = WebSquare.cookie.getCookie("userInfo");
	if (userInfoCookie) {
	    try {
	        var userInfo = JSON.parse(userInfoCookie);
	        if (userInfo && userInfo.companyId) {
	            loggedInCompanyId = userInfo.companyId;
	        }
	    } catch (e) {
	        console.error("ì‚¬ìš©ìž ì •ë³´ íŒŒì‹± ì˜¤ë¥˜:", e);
	    }
	}

	if (!loggedInCompanyId) {
	    console.warn("ë¡œê·¸ì¸ëœ companyIdë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤. ê¸°ë³¸ê°’ìœ¼ë¡œ ì„¤ì •í•˜ê±°ë‚˜ ë¡œê·¸ì¸ íŽ˜ì´ì§€ë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸í•´ì•¼ í•  ìˆ˜ ìžˆìŠµë‹ˆë‹¤.");
	    // í•„ìš”ì— ë”°ë¼ ë¡œê·¸ì¸ íŽ˜ì´ì§€ë¡œ ë¦¬ë‹¤ì´ë ‰íŠ¸í•˜ëŠ” ë¡œì§ ì¶”ê°€
	    // $p.url("/InsWebApp/ui/user/Login.xml"); 
	}
	dmp_postVo.set("companyId", loggedInCompanyId);
	console.log("ì¡°íšŒí•  companyId:", loggedInCompanyId);

	// ì„œë²„í†µì‹  - ê³µê³ ì •ë³´ ì¡°íšŒ
	$c.sbm.execute($p, sbm_selectPostVoList);
};

/**
 * ê³µê³  ëª©ë¡ ì¡°íšŒ ì™„ë£Œ
 */
scwin.sbm_postList_submitdone = function(e) {
	console.log("=== ê³µê³  ëª©ë¡ ì¡°íšŒ ì™„ë£Œ ===");
	console.log("ì‘ë‹µ ë°ì´í„°:", e.responseJSON);

	// error ìˆ˜ì‹ ì‹œ
	var elData = e.responseJSON.elData;
	var elHeader = e.responseJSON.elHeader;
	if(elHeader == null || elHeader == "" || elHeader == "undefined" || elHeader.resSuc == false) {
		console.error("ê³µê³  ëª©ë¡ ì¡°íšŒ ì—ëŸ¬:", elHeader);
		alert(`ì—ëŸ¬ì½”ë“œ : ${elHeader.resCode}\nì—ëŸ¬ë©”ì‹œì§€ : ${elHeader.resMsg}`);
		return false;
	}

	// âœ… ê³µê³ ì •ë³´ dataMap ì´ˆê¸°í™” (ìƒˆë¡œìš´ ëª©ë¡ ì¡°íšŒ ì‹œ ì´ì „ ì„ íƒ í•´ì œ)
	dmp_postVoDetail.setEmptyValue();
	console.log("ì„ íƒëœ ê³µê³  ì •ë³´ ì´ˆê¸°í™” ì™„ë£Œ");

	// ì´ ë ˆì½”ë“œ ìˆ˜ ì—…ë°ì´íŠ¸
	spn_listCnt.setLabel(elData.totalCount);

	// ë°ì´í„°ë¥¼ ê·¸ë¦¬ë“œì— ì…‹íŒ…
	dlt_postVoList.setJSON(elData.postVoList);
	console.log("âœ… GridViewì— ë°ì´í„° ë°”ì¸ë”© ì™„ë£Œ - ì´ " + dlt_postVoList.getRowCount() + "ê°œ");

	// íŽ˜ì´ì§€ ë„¤ë¹„ê²Œì´í„° ì—…ë°ì´íŠ¸
	var totalPageCount = Math.ceil(elData.totalCount / dmp_postVo.get("pageSize"));
	pgl_postList.setCount(totalPageCount);

	// í†µê³„ ì—…ë°ì´íŠ¸ (í™œì„± ê³µê³  ìˆ˜)
	tbx_activeJobs.setValue(elData.totalCount);

	// ë°ì´í„° í™•ì¸ ë¡œê·¸
	for (var i = 0; i < Math.min(dlt_postVoList.getRowCount(), 3); i++) {
		console.log("ê³µê³  " + (i+1) + ":");
		console.log("- ID:", dlt_postVoList.getCellData(i, "jobPostingId"));
		console.log("- ì œëª©:", dlt_postVoList.getCellData(i, "title"));
		console.log("- ìƒíƒœ:", dlt_postVoList.getCellData(i, "status"));
	}

	console.log("ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ ì‚­ì œí•˜ë ¤ë©´: 1) í–‰ í´ë¦­í•˜ì—¬ ê³µê³  ì„ íƒ â†’ 2) ì‚­ì œ ë²„íŠ¼ í´ë¦­");
};

/**
 * GridView í–‰ í´ë¦­ ì´ë²¤íŠ¸ (ê³µê³  ìƒì„¸ íŽ˜ì´ì§€ë¡œ ì´ë™)
 */
scwin.grd_postList_oncellclick = function(row, col) {
	console.log("=== ê³µê³  í–‰ í´ë¦­ ===");
	console.log("ì„ íƒëœ í–‰:", row);

	if (row < 0) return;

	// âœ… í‘œì¤€ íŒ¨í„´: ê·¸ë¦¬ë“œ ë°ì´í„°ë¥¼ detail DataMapì— ì§ì ‘ ë³µì‚¬
	dmp_postVoDetail.setJSON(dlt_postVoList.getRowJSON(row));

	var postData = dmp_postVoDetail.getJSON();
	console.log("ì„ íƒëœ ê³µê³  ë°ì´í„°:", postData);

	// âœ… ì„ íƒëœ í–‰ ì •ë³´ë¥¼ UIì—ì„œ í™•ì¸í•  ìˆ˜ ìžˆë„ë¡ í‘œì‹œ
	console.log("âœ… ê³µê³  ì„ íƒë¨ - ID: " + postData.jobPostingId + ", ì œëª©: " + postData.title);

	// ê³µê³  ìƒì„¸ íŽ˜ì´ì§€ë¡œ ì´ë™
	scwin.openPostDetail(postData.jobPostingId);
};

/**
 * ê³µê³  ìƒì„¸ë³´ê¸° íŽ˜ì´ì§€ë¡œ ì´ë™ (ë¬¸ì œì‹œ ì—¬ê¸° ìˆ˜ì •)
 */
scwin.openPostDetail = function (jobPostingId) {
    console.log("=== ê³µê³  ìƒì„¸ë³´ê¸° ì‹œìž‘ ===");
    console.log("í´ë¦­ëœ ê³µê³  ID:", jobPostingId);

    if (!jobPostingId || jobPostingId.trim() === "") {
        console.error("ê³µê³  IDê°€ ì—†ìŠµë‹ˆë‹¤.");
        alert("ì„ íƒëœ ê³µê³ ì˜ ì •ë³´ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
        return;
    }

    try {
        // ì›¹ìŠ¤í€˜ì–´ í™˜ê²½ì—ì„œ pageFrame ë°©ì‹ìœ¼ë¡œ íŒŒë¼ë¯¸í„° ì „ë‹¬
        if ($p.parent() && $p.parent().pf_main) {
            console.log("pageFrameì„ í†µí•œ ì´ë™:", jobPostingId);
            $p.parent().pf_main.setSrc("ui/post/PostDetail.xml", {
                dataObject: {
                    jobPostingId: jobPostingId
                }
            });
        } else {
            // ëŒ€ì•ˆ ë°©ë²•: URL ë°©ì‹
            console.log("URL ë°©ì‹ìœ¼ë¡œ ì´ë™:", jobPostingId);
            var detailUrl = "../post/PostDetail.xml?jobPostingId=" + encodeURIComponent(jobPostingId);
            $p.url(detailUrl);
        }
        console.log("âœ… íŽ˜ì´ì§€ ì´ë™ ìš”ì²­ ì™„ë£Œ");
    } catch (error) {
        console.error("âŒ íŽ˜ì´ì§€ ì´ë™ ì¤‘ ì˜¤ë¥˜:", error);
        alert("íŽ˜ì´ì§€ ì´ë™ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: " + error.message);
    }
};

/**
 * ê³µê³  ìƒì„¸ë³´ê¸°/ìˆ˜ì • UI í‘œì‹œ (ê¸°ì¡´ ëª¨ë‹¬ ë°©ì‹ - í˜„ìž¬ëŠ” ì‚¬ìš© ì•ˆí•¨)
 */
scwin.showPostDetail = function() {
	var postData = dmp_postVoDetail.getJSON();

	var message = "ï¿½ï¿½ï¿½ï¿½ï¿½ï¿½ ê³µê³  ìƒì„¸ ì •ë³´\n\n" +
				 "ID: " + (postData.jobPostingId || "ì •ë³´ì—†ìŒ") + "\n" +
				 "ì œëª©: " + (postData.title || "ì •ë³´ì—†ìŒ") + "\n" +
				 "ì„¤ëª…: " + (postData.description || "ì •ë³´ì—†ìŒ") + "\n" +
				 "ìœ„ì¹˜: " + (postData.location || "ì •ë³´ì—†ìŒ") + "\n" +
				 "ê·¼ë¬´í˜•íƒœ: " + scwin.getWorkTypeText(postData.workType) + "\n" +
				 "ìƒíƒœ: " + scwin.getStatusText(postData.status) + "\n" +
				 "ê²½ë ¥ìˆ˜ì¤€: " + (postData.experienceLevel || "ì •ë³´ì—†ìŒ") + "\n" +
				 "ê¸‰ì—¬ë²”ìœ„: " + (postData.salaryRange || "ì •ë³´ì—†ìŒ") + "\n\n" +
				 "ìˆ˜ì •í•˜ì‹œê² ìŠµë‹ˆê¹Œ?";

	// ìˆ˜ì • ì—¬ë¶€ í™•ì¸
	if (confirm(message)) {
		scwin.showEditForm(postData);
	}
};

/**
 * ìˆ˜ì • í¼ í‘œì‹œ (ê°„ë‹¨í•œ prompt ë²„ì „)
 */
scwin.showEditForm = function(postData) {
	console.log("=== ìˆ˜ì • ëª¨ë“œ ===");
	console.log("ê¸°ì¡´ ë°ì´í„°:", postData);

	var newTitle = prompt("ê³µê³  ì œëª©ì„ ìˆ˜ì •í•˜ì„¸ìš”:", postData.title || "");
	if (newTitle === null) return; // ì·¨ì†Œí•œ ê²½ìš°

	var newDescription = prompt("ê³µê³  ì„¤ëª…ì„ ìˆ˜ì •í•˜ì„¸ìš”:", postData.description || "");
	if (newDescription === null) return; // ì·¨ì†Œí•œ ê²½ìš°

	var newLocation = prompt("ê·¼ë¬´ ì§€ì—­ì„ ìˆ˜ì •í•˜ì„¸ìš”:", postData.location || "");
	if (newLocation === null) return; // ì·¨ì†Œí•œ ê²½ìš°

	// âœ… ê¸°ì¡´ ë°ì´í„°ë¥¼ ëª¨ë‘ ìœ ì§€í•˜ë©´ì„œ ìˆ˜ì •ëœ ë¶€ë¶„ë§Œ ë³€ê²½
	var updateData = {
		jobPostingId: postData.jobPostingId,				// í•„ìˆ˜: ê³µê³  ID
		companyId: postData.companyId,						// í•„ìˆ˜: íšŒì‚¬ ID  
		title: newTitle,									// ìˆ˜ì •: ì œëª©
		description: newDescription,						// ìˆ˜ì •: ì„¤ëª…
		location: newLocation,								// ìˆ˜ì •: ì§€ì—­
		// ê¸°ì¡´ ê°’ë“¤ ìœ ì§€
		requiredSkills: postData.requiredSkills || "",
		experienceLevel: postData.experienceLevel || "",
		salaryRange: postData.salaryRange || "",
		workType: postData.workType || "",
		preferredDeveloperTypes: postData.preferredDeveloperTypes || "",
		expiresAt: postData.expiresAt || "",
		postedAt: postData.postedAt || "",
		status: postData.status || "active",
		selectedTechStackNames: postData.selectedTechStackNames || "[]"
	};

	console.log("ì „ì†¡í•  ìˆ˜ì • ë°ì´í„°:", updateData);

	// ìˆ˜ì • í™•ì¸
	if (confirm("ìˆ˜ì •ëœ ë‚´ìš©ì„ ì €ìž¥í•˜ì‹œê² ìŠµë‹ˆê¹Œ?")) {
		// âœ… ì „ì²´ ë°ì´í„°ë¥¼ dmp_postVoDetailì— ì„¤ì •
		dmp_postVoDetail.setJSON(updateData);

		// ìˆ˜ì • ìš”ì²­
		$c.sbm.execute($p, sbm_updatePostVo);
	}
};

