scwin.btn_delete_onclick = function() {
	console.log("=== ì‚­ì œ ë²„íŠ¼ í´ë¦­ ===");

	// âœ… ì›¹ìŠ¤í€˜ì–´ í‘œì¤€ íŒ¨í„´: DataMapì— ì„¤ì •ëœ ë°ì´í„° í™•ì¸
	var postData = dmp_postVoDetail.getJSON();

	// ì„ íƒëœ ê³µê³ ê°€ ì—†ëŠ” ê²½ìš°
	if (!postData || !postData.jobPostingId) {
		alert("ì‚­ì œí•  ê³µê³ ë¥¼ ë¨¼ì € ì„ íƒí•´ì£¼ì„¸ìš”.\n(ëª©ë¡ì—ì„œ í–‰ì„ í´ë¦­í•˜ì—¬ ê³µê³ ë¥¼ ì„ íƒí•˜ì„¸ìš”)");
		return;
	}

	console.log("ì‚­ì œ ëŒ€ìƒ - ID:", postData.jobPostingId, "ì œëª©:", postData.title);

	// âœ… ì†Œí”„íŠ¸ ì‚­ì œ í™•ì¸ ë©”ì‹œì§€
	var confirmMsg = "'" + (postData.title || "ì„ íƒëœ ê³µê³ ") + "' ê³µê³ ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?\n\n" +
					"â€» ì‚­ì œëœ ê³µê³ ëŠ” ëª©ë¡ì—ì„œ ë³´ì´ì§€ ì•Šê²Œ ë©ë‹ˆë‹¤.\n" +
