	if (confirm(confirmMsg)) {
		// ì‚­ì œ ìš”ì²­ ì „ ë¡œê·¸
		console.log("ì†Œí”„íŠ¸ ì‚­ì œ ìš”ì²­ - ì „ì†¡ ë°ì´í„°:", postData);

