/**
 * ê·¼ë¬´í˜•íƒœ í•œê¸€ ë³€í™˜
 */
scwin.getWorkTypeText = function(workType) {
	switch(workType) {
		case 'remote': return 'ì›ê²©ê·¼ë¬´';
		case 'onsite': return 'ì¶œê·¼ê·¼ë¬´';
		case 'hybrid': return 'í•˜ì´ë¸Œë¦¬ë“œ';
		default: return workType || 'ì •ë³´ì—†ìŒ';
	}
};

/**
 * ìƒíƒœ í•œê¸€ ë³€í™˜
 */
scwin.getStatusText = function(status) {
	switch(status) {
		case 'active': return 'í™œì„±';
		case 'closed': return 'ë§ˆê°';
		case 'draft': return 'ìž„ì‹œì €ìž¥';
		default: return status || 'ì •ë³´ì—†ìŒ';
	}
};

/**
 * ë‚ ì§œ í¬ë§·íŒ…
 */
scwin.formatDate = function(dateString) {
	if (!dateString) return "-";

	try {
		var date = new Date(dateString);
		var year = date.getFullYear();
		var month = String(date.getMonth() + 1).padStart(2, '0');
		var day = String(date.getDate()).padStart(2, '0');

		return year + '-' + month + '-' + day;
	} catch (error) {
		return dateString;
	}
};
