	 * @date 2019.05.30
	 * @memberOf
	 * @param 
	 * @returns
	 * @author 
	 * @example
	 */	
	scwin.onpageload = function(e) {
		scwin.SERVER_TIME = $c.date.getServerDateTime("yyyy-MM-dd hh:mm:ss");
		spn_serverTime.setValue(scwin.SERVER_TIME);

		scwin.LOGIN_USER = $p.getParameter("userId");
		spn_user.setValue(scwin.LOGIN_USER);

        dmp_empVo.set( "pageIndex" , 1 ); // 페이지 번호
		dmp_empVo.set( "pageSize" , 10 ); // 조회건수

		scwin.execSelectEmpVoList();      // 사원 리스트 조회

	};

