	scwin.btn_reset_onclick = function(e) {

		btn_save.setStyle( "display", "" );       // 저장 버튼 생성
		btn_edit.setStyle( "display", "none" );   // 수정 버튼 제거
		btn_delete.setStyle( "display", "none" ); // 삭제 버튼 제거
		ibx_empno.setReadOnly( false );           // 사원번호 readOnly 해제
		grd_empList.removeFocusedCell();          // 셀 포커스 제거

		//------------------------------------------------------------------------
		// [DataCollection] 초기화 버튼 클릭 시 세팅된 값을 공백으로 처리한다.
		//------------------------------------------------------------------------
		// 사원 상세 정보
		dmp_empVoDetail.setEmptyValue();
/*		
		dmp_empVoDetail.set( "empno"   , "" );
		dmp_empVoDetail.set( "ename"   , "" );
		dmp_empVoDetail.set( "job"     , "" );
		dmp_empVoDetail.set( "mgr"     , "" );
		dmp_empVoDetail.set( "mgrname" , "" );
		dmp_empVoDetail.set( "hiredate", "" );
		dmp_empVoDetail.set( "sal"     , "" );
		dmp_empVoDetail.set( "comm"    , "" );
		dmp_empVoDetail.set( "deptno"  , "" );
		dmp_empVoDetail.set( "dname"   , "" );
		dmp_empVoDetail.set( "account" , "" );

		// 첨부파일 정보
		dmp_empVoDetail.set( "empno"   , "" );
		dmp_empVoDetail.set( "fileName", "" );
		dmp_empVoDetail.set( "fileData", "" );
*/		
		// 업로드 컴포넌트 초기화
		uld_file.reset();

		// 첨부파일 파일명 삭제
		spn_fileName.setValue( "" );

		// 첨부파일 영역 숨김
		grp_download.setStyle( "display", "none" );

	};

	/**
	 * 저장 버튼 클릭 이벤트
	 * @date
	 * @memberOf
	 * @param 
	 * @returns
	 * @author 
	 * @example
	 */	
	scwin.btn_save_onclick = function(e) {
		var valInfo = [{
			id : "empno",
			mandatory : true,
			label : "사원번호"
			//label :cmw.data.getLangpackMessage("CRUD_TEMPLATE_VIEW_LABEL_00009") // 패키지명
		}, {
			id : "ename",
			mandatory : true,
			label :"성명"
		}, {
			id : "job",
			mandatory : true,
			label :"직책"
		},{
			id : "hiredate",
			mandatory : true,
			label :"입사일자"
		}, {
			id : "deptno",
			mandatory : true,
			label :"부서"
		}, {
			id : "sal",
			mandatory : true,
			label :"급여"
		}];

		if ($c.data.validateGroup(grp_info, valInfo)) {
			// 사용자그룹 정보 을(를) 저장하시겠습니까?
			//var promise = $c.win.confirm(cmw.data.getLangpackMessage("COMMON_MESSAGE_00003", ["USER_GROUP_MNG_LABEL_00006"]));
			var promise = $c.win.confirm("사원정보를 저장하시겠습니까?");
			promise.then(function(isYes) {
				if(isYes) {
					//-------------------------------------------------------------------------
					// [파일 업로드] 파일업로드 실행 시 보낼 파라미터를 세팅한다.
					//-------------------------------------------------------------------------
					uld_file.addParam("empno"   , dmp_empVoDetail.get( "empno"    ) );
					uld_file.addParam("ename"   , dmp_empVoDetail.get( "ename"    ) );
					uld_file.addParam("job"     , dmp_empVoDetail.get( "job"      ) );
					uld_file.addParam("mgr"     , dmp_empVoDetail.get( "mgr"      ) );
					uld_file.addParam("hiredate", dmp_empVoDetail.get( "hiredate" ) );
					uld_file.addParam("sal"     , dmp_empVoDetail.get( "sal"      ) );
					uld_file.addParam("comm"    , dmp_empVoDetail.get( "comm"     ) );
					uld_file.addParam("deptno"  , dmp_empVoDetail.get( "deptno"   ) );
					uld_file.addParam("account" , dmp_empVoDetail.get( "account"  ) );

					uld_file.changeFormAction(scwin.EMP_FILE_SAVE_PATH);
					uld_file.submit();
					uld_file.setCallback(scwin.execInsertEmpVo());
				}
			});
		}
	};

	/**
	 * 수정 버튼 클릭 이벤트
	 * @date 2019.05.30
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */		
	scwin.btn_edit_onclick = function(e) {
		var promise = $c.win.confirm(dmp_empVoDetail.get("empno") + " 사원 정보를 수정하시겠습니까?");
		promise.then(function(isYes) {
			if(isYes) {
				//-------------------------------------------------------------------------
				// [파일 업로드] 파일업로드 실행 시 보낼 파라미터를 세팅한다.
				//-------------------------------------------------------------------------
				uld_file.addParam( "empno"    , dmp_empVoDetail.get( "empno"    ) );
				uld_file.addParam( "ename"    , dmp_empVoDetail.get( "ename"    ) );
				uld_file.addParam( "job"      , dmp_empVoDetail.get( "job"      ) );
				uld_file.addParam( "mgr"      , dmp_empVoDetail.get( "mgr"      ) );
				uld_file.addParam( "hiredate" , dmp_empVoDetail.get( "hiredate" ) );
				uld_file.addParam( "sal"      , dmp_empVoDetail.get( "sal"      ) );
				uld_file.addParam( "comm"     , dmp_empVoDetail.get( "comm"     ) );
				uld_file.addParam( "deptno"   , dmp_empVoDetail.get( "deptno"   ) );
				uld_file.addParam( "account"  , dmp_empVoDetail.get( "account"  ) );
				uld_file.addParam( "fileName" , dmp_empFileVo.get("fileName") );

				uld_file.changeFormAction(scwin.EMP_FILE_EDIT_PATH);
				uld_file.submit();
				uld_file.setCallback(scwin.sbm_updateEmpVo_submitdone());
			}
		});		
	};

	/**
	 * 삭제 버튼 클릭 이벤트
	 * @date 2019.05.30
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */	
	scwin.btn_delete_onclick = function(e) {
		var empno = dmp_empVoDetail.get( "empno" );

		if( $c.util.isEmpty(empno) ) {
			$c.win.alert("삭제할 대상이 없습니다. 사원을 선택하세요.");
			return false;
		}

		var promise = $c.win.confirm(empno + " 사원 정보를 삭제하시겠습니까?");
		promise.then(function(isYes) {
			if(isYes) {
				scwin.execDeleteEmpVo();
			}
		});			
	};

