	/************************** 콜백 함수 *************************/

	/**
	 * 사원 리스트 조회 콜백
	 * @date 2019.05.30
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_empList_submitdone = function(e) {
        // debugger;

		console.log("-------------------- scwin.sbm_empList_submitdone --------------------");
		console.log( e.responseJSON);
		console.log("----------------------------------------------------------------------");

		var elHeader  = e.responseJSON.elHeader;
		//var empVo     = e.responseJSON.empVo;
		var empVoList = e.responseJSON.elData.empVoList;

		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			alert("- 에러 코드 : " + elHeader.resCode + "\n- 에러 메시지 : " + elHeader.resMsg);
			return false;
		}

        dlt_empVoList.removeAll();
        dlt_empVoList.reform();
		dlt_empVoList.setJSON(empVoList); // 데이터를 그리드에 세팅한다.
		pgl_empList.setCount(e.responseJSON.elData.totalPageCount, true); // 페이지 리스트에 인덱스를 세팅한다.
		pgl_empList.setSelectedIndex(dmp_empVo.get( "pageIndex"), true); // 페이지 리스트에 인덱스를 세팅한다.

		var pageSize = dmp_empVo.get("pageSize");
		var pageIndex = dmp_empVo.get("pageIndex");
		grd_empList.setStartRowNumber(pageSize * pageIndex - pageSize);

	};

	/**
	 * 사원 정보 조회 콜백
	 * @date 2019.06.03
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_selectEmpVo_submitdone = function(e) {

		console.log("------------------ scwin.sbm_selectEmpVo_submitdone ------------------");
		console.log(e.responseJSON);
		console.log("----------------------------------------------------------------------");

		var elHeader  = e.responseJSON.elHeader;

		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			alert("- 에러 코드 : " + elHeader.resCode + "\n- 에러 메시지 : " + elHeader.resMsg);
			return false;
		}

		dmp_empVoDetail.setJSON(e.responseJSON.elData.empVo);   // 사원정보 매핑
		dmp_empFileVo.setJSON(e.responseJSON.elData.empFileVo); // 파일정보 매핑

		var tmpFileName = spn_fileName.getValue();
		if(tmpFileName != "" && tmpFileName != null) {
			grp_download.setStyle("display", "");
		} else {
			grp_download.setStyle("display", "none");
		}

	};

	/**
	 * 사원 정보 등록 콜백
	 * @date 2019.05.31
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_insertEmpVo_submitdone = function(e) {

		console.log("------------------ scwin.sbm_insertEmpVo_submitdone ------------------");
		console.log(e.responseJSON);
		console.log("----------------------------------------------------------------------");

		var elHeader  = e.responseJSON.elHeader;

		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			alert("- 에러 코드 : " + elHeader.resCode + "\n- 에러 메시지 : " + elHeader.resMsg);
			return false;
		}

		alert("사원정보가 등록되었습니다.");
		scwin.btn_reset_onclick();
		$c.util.setTimeout(function() { scwin.execSelectEmpVoList(); }, 500); // 0.5초 경과 후 실행

	};

	/**
	 * 사원 정보 수정 콜백
	 * @date
	 * @memberOf
	 * @param 
	 * @returns
	 * @author 
	 * @example
	 */
	scwin.sbm_updateEmpVo_submitdone = function(e) {

//		console.log("------------------ scwin.sbm_updateEmpVo_submitdone ------------------");
//		console.log(e.responseJSON);
//		console.log("----------------------------------------------------------------------");

		alert("사원정보가 수정되었습니다.");
		scwin.btn_reset_onclick(); // 입력 폼 초기화

		$c.util.setTimeout(function() { scwin.execSelectEmpVoList(); }, 500); // 0.5초 경과 후 실행

	};

	/**
	 * 사원 정보 삭제 콜백
	 * @date 2019.05.31
	 * @memberOf
	 * @param 
	 * @returns 
	 * @author 
	 * @example
	 */
	scwin.sbm_deleteEmpVo_submitdone = function(e) {

		console.log("------------------ scwin.sbm_deleteEmpVo_submitdone ------------------");
		console.log(e.responseJSON);
		console.log("----------------------------------------------------------------------");

		var submission = $p.getSubmission("sbm_deleteEmpVo");

		if(e.id == submission.id) {
			alert("사원정보가 삭제되었습니다.");
			scwin.btn_reset_onclick();   // 입력 폼 초기화
			$c.util.setTimeout(function() { scwin.execSelectEmpVoList(); }, 500); // 0.5초 경과 후 실행
		}

	};

