	 * @example
	 */
	scwin.sbm_logout_submitdone = function(e) {

//		console.log("-------------------- scwin.sbm_logout_submitdone ---------------------");
//		console.log(e.responseJSON);
//		console.log("----------------------------------------------------------------------");

		var elHeader = e.responseJSON.elHeader;

		if(elHeader == null || elHeader == "" || elHeader == "undefiend" || elHeader.resSuc == false) {
			alert("- 에러 코드 : " + elHeader.resCode + "\n- 에러 메시지 : " + elHeader.resMsg);
			return false;
		}

		scwin.redirectPage(scwin.EMP_LOGIN_VIEW_PATH);

	};
