scwin.goBackToList = function() {
    try {
        if ($p.parent() && $p.parent().pf_main) {
            $p.parent().pf_main.setSrc("../corporate/RecruitmentDashboard.xml");
        } else {
            $p.url("../corporate/RecruitmentDashboard.xml");
        }
    } catch (e) {
        console.error("í˜ì´ì§€ ì´ë™ ì¤‘ ì˜¤ë¥˜:", e);
        $p.url("../corporate/RecruitmentDashboard.xml");
    }
};

