/**
 * ìˆ˜ì • ë²„íŠ¼ í´ë¦­
 */
scwin.btnEdit_onclick = function(e) {
    var jobPostingId = dmp_postDetail.get("jobPostingId");

    if (!jobPostingId) {
        $c.win.alert("ê³µê³  IDë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
        return;
    }

    console.log("ê³µê³  ìˆ˜ì • í˜ì´ì§€ë¡œ ì´ë™ - ID:", jobPostingId);

    try {
        // ìˆ˜ì • í˜ì´ì§€ë¡œ ì´ë™ (CreatePosting.xmlì„ ìˆ˜ì • ëª¨ë“œë¡œ)
        var params = {
            mode: "edit",
            jobPostingId: jobPostingId
        };

        if ($p.parent() && $p.parent().pf_main) {
            $p.parent().pf_main.setSrc("CreatePosting.xml", { dataObject: params });
        } else {
            $p.url("CreatePosting.xml?mode=edit&jobPostingId=" + encodeURIComponent(jobPostingId));
        }
    } catch (e) {
        console.error("ìˆ˜ì • í˜ì´ì§€ ì´ë™ ì¤‘ ì˜¤ë¥˜:", e);
        $c.win.alert("ìˆ˜ì • í˜ì´ì§€ë¡œ ì´ë™ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.");
    }
};

