scwin.btnDelete_onclick = function(e) {
    var jobPostingId = dmp_postDetail.get("jobPostingId");
    var title = dmp_postDetail.get("title") || "ì´ ê³µê³ ";

    if (!jobPostingId) {
        $c.win.alert("ê³µê³  IDë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.");
        return;
    }

    var confirmMsg = "ì •ë§ë¡œ \"" + title + "\" ê³µê³ ë¥¼ ì‚­ì œí•˜ì‹œê² ìŠµë‹ˆê¹Œ?\n\nì‚­ì œëœ ê³µê³ ëŠ” ë³µêµ¬í•  ìˆ˜ ì—†ìŠµë‹ˆë‹¤.";

    $c.win.confirm(confirmMsg).then(function(result) {
        if (result) {
            console.log("ê³µê³  ì‚­ì œ ì‹¤í–‰ - ID:", jobPostingId);

            // ì‚­ì œ íŒŒë¼ë¯¸í„° ì„¤ì •
            dmp_deleteParam.set("jobPostingId", jobPostingId);

            // ì‚­ì œ ì‹¤í–‰
            $c.sbm.execute($p, sbm_deletePost);
        }
    });
};

