scwin.onpageload = function () {
    // window Í∞ùÏ≤¥Ïóê formatter Ìï®Ïàò Îì±Î°ù
    window.scwin = window.scwin || {};
    window.scwin.openResumePreview = scwin.openResumePreview;
    window.scwin.displayResumeFileName = scwin.displayResumeFileName;

    // ÌöåÏÇ¨ ÏïÑÏù¥ÎîîÎ°ú Ïú†Ï†Ä Î∂àÎü¨Ïò§Í∏∞
    scwin.getJobPostingId();

    // Ïä§Ïπ¥ÏõÉ ÏÉÅÌÉú ÌïÑÌÑ∞ Í∏∞Î≥∏Í∞í ÏÑ§Ï†ï
    scoutStatusFilter.setValue("NOT_SCOUTED");

    // Í∑∏Î¶¨ÎìúÎ∑∞ Í∞±Ïã†ÌïòÍ∏∞
    setTimeout(function() {
        if (gridView1) {
            console.log("Ïä§Ïπ¥ÏõÉ ÌéòÏù¥ÏßÄ Í∑∏Î¶¨ÎìúÎ∑∞ ÏÉàÎ°úÍ≥†Ïπ®");
            gridView1.refresh();
