scwin.w2selectbox_disabled_onselected = function () {
    // Ïø†ÌÇ§ÏóêÏÑú userMbti Í∞ÄÏ†∏Ïò§Í∏∞
    var userInfoCookie = WebSquare.cookie.getCookie("userInfo");
    if (!userInfoCookie) {
        alert("Î°úÍ∑∏Ïù∏ ÌõÑ ÏÇ¨Ïö©Í∞ÄÎä•Ìïú Í∏∞Îä•ÏûÖÎãàÎã§.");
        $p.url("../commom/MainPage.xml");
    }

    var userInfo;
    try {
        userInfo = JSON.parse(userInfoCookie);
    } catch (e) {
        console.error("ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÌååÏã± Ïò§Î•ò:", e);
        return; // Ìï®Ïàò Ï¢ÖÎ£å
    }

    // userInfo.mbtiÍ∞Ä Ï°¥Ïû¨ÌïòÎäîÏßÄ ÌôïÏù∏
    if (userInfo.role !== 'COMPANY') {
        $c.win.alert("ÏûòÎ™ªÎêú Ï†ëÍ∑ºÏûÖÎãàÎã§. Îã§Ïãú Î°úÍ∑∏Ïù∏ Ìï¥Ï£ºÏÑ∏Ïöî.");
        $p.url("../commom/MainPage.xml");
    }

    var selectedValue = w2selectbox_disabled.getValue();

    // DataMapÏóê Í∞í ÏÑ§Ï†ï
    dmp_scoutVo.set("mbtiMatchFilter", selectedValue);
    dmp_scoutVo.set("pageIndex", "1");

    // API Ìò∏Ï∂ú
    $c.sbm.execute($p, sbm_selectScoutVo);
};

scwin.sbm_scoutVo_submitdone = function (e) {
    console.log("Ïä§Ïπ¥ÏõÉ Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå ÏôÑÎ£å", e);

    try {
        if (e && e.responseJSON && e.responseJSON.elData && e.responseJSON.elData.scoutDetailVo) {
            var data = e.responseJSON.elData.scoutDetailVo;
            console.log("Ïä§Ïπ¥ÏõÉ ÏÉÅÏÑ∏ Îç∞Ïù¥ÌÑ∞:", data);

            // Îç∞Ïù¥ÌÑ∞ Î°úÎìú ÌõÑ Ïª§Ïä§ÌÖÄ Î†åÎçîÎü¨ Ïû¨Ï†ÅÏö©
            setTimeout(function() {
                console.log("Ïù¥Î†•ÏÑú ÌïÑÎìú Î†åÎçîÎßÅ ÏãúÏûë");
                var rows = gridView1.getRowCount();
                console.log("Í∑∏Î¶¨Îìú Ìñâ Ïàò:", rows);

                for (var i = 0; i < rows; i++) {
                    var resumeFileName = dlt_scoutListVo.getCellData(i, "resumeFileName");
                    console.log("Ìñâ " + i + " Ïù¥Î†•ÏÑú ÌååÏùºÎ™Ö:", resumeFileName);
                }

                // Ï†ÑÏ≤¥ Í∑∏Î¶¨Îìú ÏÉàÎ°úÍ≥†Ïπ®
                gridView1.refresh();
            }, 200);
        }
    } catch (error) {
        console.error("Ïä§Ïπ¥ÏõÉ Îç∞Ïù¥ÌÑ∞ Ï≤òÎ¶¨ Ï§ë Ïò§Î•ò:", error);
    }
};

// Ïù¥Î†•ÏÑú ÌïÑÎìúÏóê ÎåÄÌïú formatter Ìï®Ïàò Íµ¨ÌòÑ
scwin.displayResumeFileName = function(value) {
    console.log("displayResumeFileName Ìò∏Ï∂úÎê®, value:", value);

    // Ïù¥Î†•ÏÑú ÌååÏùºÎ™ÖÏù¥ ÏûàÎäîÏßÄ ÌôïÏù∏
    var hasResume = value && value.trim() !== "";

    if (hasResume) {
        return "<span class='resume-link-text'>Ïù¥Î†•ÏÑú Î≥¥Í∏∞</span>";
    } else {
        return "<span class='resume-none-text'>Ïù¥Î†•ÏÑú ÏóÜÏùå</span>";
    }
};

// Í∑∏Î¶¨Îìú ÏÖÄ ÌÅ¥Î¶≠ Ïù¥Î≤§Ìä∏ (Ïù¥Î†•ÏÑú Ïª¨Îüº)
scwin.gridView1_oncellclick = function(row, col, colId) {
    console.log("Í∑∏Î¶¨Îìú ÏÖÄ ÌÅ¥Î¶≠: row=" + row + ", col=" + col + ", colId=" + colId);

    if (colId === "resumeFileName") {
        // Ìï¥Îãπ ÌñâÏùò Ïù¥Î†•ÏÑú ÌååÏùºÎ™Ö ÌôïÏù∏
        var resumeFileName = dlt_scoutListVo.getCellData(row, "resumeFileName");

        // Ïù¥Î†•ÏÑúÍ∞Ä ÏóÜÏúºÎ©¥ Î¨¥Ïãú
        if (!resumeFileName || resumeFileName.trim() === "") {
            console.log("Ïù¥Î†•ÏÑú ÌååÏùºÏù¥ ÏóÜÏäµÎãàÎã§.");
            return;
        }

        console.log("Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞ Ïó¥Í∏∞ ÏãúÎèÑ: " + resumeFileName);
        scwin.openResumePreview(row);
    }
};

// Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞ ÌåùÏóÖ Ïó¥Í∏∞
scwin.openResumePreview = function(rowIndex) {
    try {
        console.log("Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞ ÌåùÏóÖ Ïó¥Í∏∞ - Ìñâ Ïù∏Îç±Ïä§:", rowIndex);

        // ÏÑ†ÌÉùÎêú ÌñâÏùò Îç∞Ïù¥ÌÑ∞ Í∞ÄÏ†∏Ïò§Í∏∞
        var accountId = dlt_scoutListVo.getCellData(rowIndex, "accountId");
        var userName = dlt_scoutListVo.getCellData(rowIndex, "name");
        var resumeFileName = dlt_scoutListVo.getCellData(rowIndex, "resumeFileName");

        console.log("ÏÑ†ÌÉùÎêú ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥:", {
            accountId: accountId,
            userName: userName,
            resumeFileName: resumeFileName
        });

        // Ïù¥Î†•ÏÑúÍ∞Ä ÏóÜÎäî Í≤ΩÏö∞
        if (!resumeFileName || resumeFileName.trim() === "") {
            $c.win.alert("Îì±Î°ùÎêú Ïù¥Î†•ÏÑúÍ∞Ä ÏóÜÏäµÎãàÎã§.");
            return;
        }

        // ÌåùÏóÖÏóê Ï†ÑÎã¨Ìï† Îç∞Ïù¥ÌÑ∞
        var popupData = {
            accountId: accountId,
            userName: userName || "ÏÇ¨Ïö©Ïûê",
            resumeFileName: resumeFileName
        };

        // Ï†ÑÏó≠ Î≥ÄÏàòÎ°ú ÌååÎùºÎØ∏ÌÑ∞ Ï†ÄÏû•
        window.scwin.paramData = popupData;

        // ÏÑ∏ÏÖò Ïä§ÌÜ†Î¶¨ÏßÄÏóêÎèÑ Ï†ÄÏû•
        try {
            sessionStorage.setItem("resumePreviewParams", JSON.stringify(popupData));
        } catch(e) {
            console.error("ÏÑ∏ÏÖò Ïä§ÌÜ†Î¶¨ÏßÄ Ï†ÄÏû• Ïò§Î•ò:", e);
        }

        // ÌåùÏóÖ ÏòµÏÖò
        var popupOptions = {
            id: "resume_preview_popup_" + accountId,
            type: "wframePopup", // pageFramePopupÏóêÏÑú wframePopupÏúºÎ°ú Î≥ÄÍ≤Ω
            width: "900px",
            height: "700px",
            modal: true,
            popupName: "Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞",
            resizable: true
        };

        console.log("ÌåùÏóÖ Îç∞Ïù¥ÌÑ∞:", popupData);
        console.log("ÌåùÏóÖ ÏòµÏÖò:", popupOptions);

        // ÌåùÏóÖ Ïó¥Í∏∞
        $c.win.openPopup("../common/ResumePreviewPopup.xml", popupOptions, popupData);

    } catch (error) {
        console.error("Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞ ÌåùÏóÖ Ïó¥Í∏∞ Ï§ë Ïò§Î•ò:", error);
        $c.win.alert("Ïù¥Î†•ÏÑú ÎØ∏Î¶¨Î≥¥Í∏∞Î•º Ïó¥ Ïàò ÏóÜÏäµÎãàÎã§: " + error.message);
    }
};
