    if (hasResume) {
        return "<span class='resume-link-text'>ì´ë ¥ì„œ ë³´ê¸°</span>";
    } else {
        return "<span class='resume-none-text'>ì´ë ¥ì„œ ì—†ìŒ</span>";
    }
};

