scwin.gridView1_oncellclick = function (row, col, colId) {
    console.log("ê·¸ë¦¬ë“œ ì…€ í´ë¦­: row=" + row + ", col=" + col + ", colId=" + colId);

    if (colId === "resumeFileName") {
        // í•´ë‹¹ í–‰ì˜ ì´ë ¥ì„œ íŒŒì¼ëª… í™•ì¸
        var resumeFileName = dlt_scoutListVo.getCellData(row, "resumeFileName");

        // ì´ë ¥ì„œê°€ ì—†ìœ¼ë©´ ë¬´ì‹œ
        if (!resumeFileName || resumeFileName.trim() === "") {
            console.log("ì´ë ¥ì„œ íŒŒì¼ì´ ì—†ìŠµë‹ˆë‹¤.");
            return;
        }

        console.log("ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸° ì—´ê¸° ì‹œë„: " + resumeFileName);
        scwin.openResumePreview(row);
    }
};

// ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸° íŒì—… ì—´ê¸°
scwin.openResumePreview = function (rowIndex) {
    try {
        console.log("ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸° íŒì—… ì—´ê¸° - í–‰ ì¸ë±ìŠ¤:", rowIndex);

        // ì„ íƒëœ í–‰ì˜ ë°ì´í„° ê°€ì ¸ì˜¤ê¸°
        var accountId = dlt_scoutListVo.getCellData(rowIndex, "accountId");
        var userName = dlt_scoutListVo.getCellData(rowIndex, "name");
        var resumeFileName = dlt_scoutListVo.getCellData(rowIndex, "resumeFileName");

        console.log("ì„ íƒëœ ì‚¬ìš©ì ì •ë³´:", {
            accountId: accountId,
            userName: userName,
            resumeFileName: resumeFileName
        });

        // ì´ë ¥ì„œê°€ ì—†ëŠ” ê²½ìš°
        if (!resumeFileName || resumeFileName.trim() === "") {
            $c.win.alert("ë“±ë¡ëœ ì´ë ¥ì„œê°€ ì—†ìŠµë‹ˆë‹¤.");
            return;
        }

        // íŒì—…ì— ì „ë‹¬í•  ë°ì´í„°
        var popupData = {
            accountId: accountId,
            userName: userName || "ì‚¬ìš©ì",
            resumeFileName: resumeFileName
        };

        // ì „ì—­ ë³€ìˆ˜ë¡œ íŒŒë¼ë¯¸í„° ì €ì¥
        window.scwin.paramData = popupData;

        // ì„¸ì…˜ ìŠ¤í† ë¦¬ì§€ì—ë„ ì €ì¥
        try {
            sessionStorage.setItem("resumePreviewParams", JSON.stringify(popupData));
        } catch (e) {
            console.error("ì„¸ì…˜ ìŠ¤í† ë¦¬ì§€ ì €ì¥ ì˜¤ë¥˜:", e);
        }

        // íŒì—… ì˜µì…˜
        var popupOptions = {
            id: "resume_preview_popup_" + accountId,
            type: "wframePopup", // pageFramePopupì—ì„œ wframePopupìœ¼ë¡œ ë³€ê²½
            width: "900px",
            height: "700px",
            modal: true,
            popupName: "ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸°",
            resizable: true
        };

        console.log("íŒì—… ë°ì´í„°:", popupData);
        console.log("íŒì—… ì˜µì…˜:", popupOptions);

        // íŒì—… ì—´ê¸°
        $c.win.openPopup("../common/ResumePreviewPopup.xml", popupOptions, popupData);

    } catch (error) {
        console.error("ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸° íŒì—… ì—´ê¸° ì¤‘ ì˜¤ë¥˜:", error);
        $c.win.alert("ì´ë ¥ì„œ ë¯¸ë¦¬ë³´ê¸°ë¥¼ ì—´ ìˆ˜ ì—†ìŠµë‹ˆë‹¤: " + error.message);
    }
};

// ê¸°ìˆ ìŠ¤íƒ í•„í„° ì´ˆê¸°í™” í•¨ìˆ˜
scwin.initTechStackFilter = function () {
    var rowCount = gridView1.getRowCount();
    var techStackList = [];
    for (var i = 0; i < rowCount; i++) {
        var techStackData = dlt_scoutListVo.getCellData(i, "techStack");
        if (techStackData && techStackData.trim() !== "") {
            // ì‰¼í‘œë¡œ êµ¬ë¶„ëœ ê¸°ìˆ ìŠ¤íƒë“¤ì„ ë¶„ë¦¬
            var techStacks = techStackData.split(",");
            for (var j = 0; j < techStacks.length; j++) {
                var techStack = techStacks[j].trim();
                console.log("techStack : " + techStack);
                if (techStack !== "" && techStackList.indexOf(techStack) === -1) {
                    techStackList.push(techStack);
                }
            }
        }
    }
    console.log("rowCount : " + rowCount);
    console.log("techStackList : " + techStackList);

    // ê¸°ìˆ ìŠ¤íƒ ëª©ë¡ ì •ë ¬
    techStackList.sort();

    // checkcomboboxì— ì˜µì…˜ ì¶”ê°€
    // msb_techStackFilter.clear(); // ê¸°ì¡´ ì•„ì´í…œ ì´ˆê¸°í™”
    for (var k = 0; k < techStackList.length; k++) {
        msb_techStackFilter.addItem(techStackList[k], techStackList[k]);
    }
};

// ê¸°ìˆ ìŠ¤íƒ í•„í„° ì ìš© í•¨ìˆ˜
scwin.applyTechStackFilter = function () {
    var selectedTechStacks = msb_techStackFilter.getValue();
    console.log("selectedTechStacks from checkcombobox:", selectedTechStacks);

    var selectedArray = [];
    if (typeof selectedTechStacks === 'string' && selectedTechStacks !== "") {
        selectedArray = selectedTechStacks.split(",").map(function(item) { return item.trim().toLowerCase(); });
    } else if (Array.isArray(selectedTechStacks)) {
        selectedArray = selectedTechStacks.map(function(item) { return item.trim().toLowerCase(); });
    }
    // Convert selectedArray to a Set for efficient lookups
    var selectedTechSet = new Set(selectedArray);

    if (selectedTechSet.size === 0) {
        // ì„ íƒëœ ê¸°ìˆ ìŠ¤íƒì´ ì—†ìœ¼ë©´ ëª¨ë“  í–‰ í‘œì‹œ
        var rowCount = dlt_scoutListVo.getRowCount();
        for (var i = 0; i < rowCount; i++) {
            gridView1.setRowVisible(i, true);
        }
        return;
    }

    var dataList = dlt_scoutListVo;
    var rowCount = dataList.getRowCount();

    // í•„í„°ë§ ë¡œì§
    for (var i = 0; i < rowCount; i++) {
        var techStackData = dataList.getCellData(i, "techStack");
        var showRow = false;

        if (techStackData && techStackData.trim() !== "") {
            // Prepare rowTechStacks: split, trim, and lowercase all at once
            var rowTechStacks = techStackData.split(",").map(function(item) { return item.trim().toLowerCase(); });
            // Convert rowTechStacks to a Set for efficient lookups
            var rowTechSet = new Set(rowTechStacks);

            // Check if any selected tech stack is present in the row's tech stacks
            for (let selectedTech of selectedTechSet) {
                if (rowTechSet.has(selectedTech)) {
                    showRow = true;
                    break; // Found a match, no need to check other selectedTechs for this row
                }
            }
        }

        // í–‰ í‘œì‹œ/ìˆ¨ê¹€ ì„¤ì •
        if (showRow) {
            gridView1.setRowVisible(i, true);
        } else {
            gridView1.setRowVisible(i, false);
        }
    }
};

// ê¸°ìˆ ìŠ¤íƒ í•„í„° ì´ˆê¸°í™” ë²„íŠ¼ (ì„ íƒì‚¬í•­)
scwin.btn_resetFilter_onclick = function () {
    msb_techStackFilter.setValue(""); // checkcomboboxì˜ ì„ íƒ ì´ˆê¸°í™”
    var rowCount = dlt_scoutListVo.getRowCount();
    for (var i = 0; i < rowCount; i++) {
        gridView1.setRowVisible(i, true);
    }
};
