head.script:
scwin.onpageload = function() {
    console.log("=== PostDetail ÌéòÏù¥ÏßÄ Î°úÎìú ===");

    var jobPostingId = null;

    // 1. dataObjectÏóêÏÑú ÌååÎùºÎØ∏ÌÑ∞ Ï∂îÏ∂ú ÏãúÎèÑ (pageFrame.setSrcÎ°ú Ï†ÑÎã¨Îêú Í≤ΩÏö∞)
    try {
        if ($p.getParameter && $p.getParameter("dataObject")) {
            var dataObject = $p.getParameter("dataObject");
            if (dataObject && dataObject.jobPostingId) {
                jobPostingId = dataObject.jobPostingId;
                console.log("dataObjectÏóêÏÑú jobPostingId Ï∂îÏ∂ú:", jobPostingId);
            }
        }
    } catch (e) {
        console.log("dataObject Ï∂îÏ∂ú Ïã§Ìå®:", e);
    }

    // 2. URL ÏøºÎ¶¨Ïä§Ìä∏ÎßÅÏóêÏÑú ÌååÎùºÎØ∏ÌÑ∞ Ï∂îÏ∂ú ÏãúÎèÑ (URL Î∞©ÏãùÏúºÎ°ú Ï†ÑÎã¨Îêú Í≤ΩÏö∞)
    if (!jobPostingId) {
        jobPostingId = $c.util.getParameter("jobPostingId");
        console.log("URLÏóêÏÑú jobPostingId Ï∂îÏ∂ú:", jobPostingId);
    }

    // 3. ÏßÅÏ†ë ÏøºÎ¶¨Ïä§Ìä∏ÎßÅ ÌååÏã± ÏãúÎèÑ
    if (!jobPostingId) {
        try {
            var urlParams = new URLSearchParams(window.location.search);
            jobPostingId = urlParams.get('jobPostingId');
            console.log("URLSearchParamsÏóêÏÑú jobPostingId Ï∂îÏ∂ú:", jobPostingId);
        } catch (e) {
            console.log("URLSearchParams ÌååÏã± Ïã§Ìå®:", e);
        }
    }

    // 4. window.location.hrefÏóêÏÑú ÏßÅÏ†ë ÌååÏã± ÏãúÎèÑ
    if (!jobPostingId) {
        try {
            var href = window.location.href;
            var match = href.match(/[?&]jobPostingId=([^&]*)/);
            if (match && match[1]) {
                jobPostingId = decodeURIComponent(match[1]);
                console.log("location.hrefÏóêÏÑú jobPostingId Ï∂îÏ∂ú:", jobPostingId);
            }
        } catch (e) {
            console.log("location.href ÌååÏã± Ïã§Ìå®:", e);
        }
    }

    console.log("ÏµúÏ¢Ö Ï∂îÏ∂úÎêú jobPostingId:", jobPostingId);

    if (jobPostingId && jobPostingId.trim() !== "") {
        // ÌååÎùºÎØ∏ÌÑ∞ ÏÑ§Ï†ïÌïòÍ≥† ÏÑúÎ≤ÑÏóêÏÑú Ïã§Ï†ú Îç∞Ïù¥ÌÑ∞ Ï°∞Ìöå
        dmp_postParam.set("jobPostingId", jobPostingId);
        console.log("ÏÑúÎ≤Ñ Ï°∞Ìöå ÏãúÏûë...");
        $c.sbm.execute($p, sbm_selectPostDetail);
    } else {
        console.warn("jobPostingId ÌååÎùºÎØ∏ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.");
        alert("ÏûòÎ™ªÎêú Ï†ëÍ∑ºÏûÖÎãàÎã§. Í≥µÍ≥† Î™©Î°ùÏúºÎ°ú Ïù¥ÎèôÌï©ÎãàÎã§.");
        scwin.goBackToList();
    }
};

