        // ÏóêÎü¨ Ïãú Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
        setTimeout(function() {
            scwin.goBackToList();
        }, 1000);
        return false;
    }

    // Îç∞Ïù¥ÌÑ∞ Ï°¥Ïû¨ Ïó¨Î∂Ä Ï≤¥ÌÅ¨
    if (!elData) {
        console.error("Ï°∞ÌöåÎêú Îç∞Ïù¥ÌÑ∞Í∞Ä ÏóÜÏäµÎãàÎã§.");
        alert("Ìï¥Îãπ Í≥µÍ≥† Ï†ïÎ≥¥Î•º Ï∞æÏùÑ Ïàò ÏóÜÏäµÎãàÎã§.\nÍ≥µÍ≥†Í∞Ä ÏÇ≠Ï†úÎêòÏóàÍ±∞ÎÇò Ï°¥Ïû¨ÌïòÏßÄ ÏïäÏùÑ Ïàò ÏûàÏäµÎãàÎã§.");

        setTimeout(function() {
            scwin.goBackToList();
        }, 1000);
        return false;
    }

    console.log("Ï°∞ÌöåÎêú Í≥µÍ≥† Ï†ïÎ≥¥:");
    console.log("- Í≥µÍ≥† ID:", elData.jobPostingId);
    console.log("- Ï†úÎ™©:", elData.title);
    console.log("- ÌöåÏÇ¨Î™Ö:", elData.companyName);
    console.log("- ÏÉÅÌÉú:", elData.status);

    // ÎπÑÌôúÏÑ±ÌôîÎêú Í≥µÍ≥† Ï≤¥ÌÅ¨
    if (elData.status === 'inactive' || elData.status === 'deleted') {
        console.warn("ÎπÑÌôúÏÑ±ÌôîÎêú Í≥µÍ≥†ÏûÖÎãàÎã§.");
        alert("Ïù¥ Í≥µÍ≥†Îäî ÌòÑÏû¨ Î™®ÏßëÏù¥ Ï§ëÎã®ÎêòÏóàÏäµÎãàÎã§.");

        setTimeout(function() {
            scwin.goBackToList();
        }, 1000);
        return false;
    }

    try {
        // Îç∞Ïù¥ÌÑ∞Î•º DataMapÏóê ÏÑ§Ï†ï
        dmp_postDetail.setJSON(elData);

        // ÌôîÎ©¥ ÏóÖÎç∞Ïù¥Ìä∏
        scwin.updateUI();

        console.log("‚úÖ Í≥µÍ≥† ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Î∞îÏù∏Îî© ÏôÑÎ£å");

    } catch (error) {
        console.error("Îç∞Ïù¥ÌÑ∞ Î∞îÏù∏Îî© Ï§ë Ïò§Î•ò:", error);
        alert("Îç∞Ïù¥ÌÑ∞Î•º ÌôîÎ©¥Ïóê ÌëúÏãúÌïòÎäî Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
    }
};

