scwin.updateJobMeta = function() {
    var companyName = dmp_postDetail.get("companyName") || "Ï†ïÎ≥¥ÏóÜÏùå";
    var experienceLevel = scwin.getExperienceLevelText(dmp_postDetail.get("experienceLevel"));
    var location = dmp_postDetail.get("location") || "Ï†ïÎ≥¥ÏóÜÏùå";
    var workType = scwin.getWorkTypeText(dmp_postDetail.get("workType"));

    var metaText = companyName + " ‚Ä¢ " + experienceLevel + " ‚Ä¢ " + location + " ‚Ä¢ " + workType;

    // Î©îÌÉÄ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    var metaElement = document.querySelector('.job-meta');
    if (metaElement) {
        metaElement.textContent = metaText;
    }

    console.log("Î©îÌÉÄ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏:", metaText);
};

/**
 * Í∏∞Ïà†Ïä§ÌÉù ÌÉúÍ∑∏ ÎèôÏ†Å ÏÉùÏÑ±
 */
scwin.updateTechStackTags = function() {
    var techStackJson = dmp_postDetail.get("selectedTechStackNames");
    var tagsContainer = document.querySelector('.tech-tags');

    if (tagsContainer && techStackJson) {
        try {
            var techStacks = JSON.parse(techStackJson);
            var tagsHtml = '';

            for (var i = 0; i < techStacks.length; i++) {
                tagsHtml += '<span class="tag tech-tag">' + techStacks[i] + '</span>';
            }

            tagsContainer.innerHTML = tagsHtml;
            console.log("Í∏∞Ïà†Ïä§ÌÉù ÌÉúÍ∑∏ ÏÉùÏÑ± ÏôÑÎ£å:", techStacks.length + "Í∞ú");
        } catch (e) {
            console.error("Í∏∞Ïà†Ïä§ÌÉù JSON ÌååÏã± Ïò§Î•ò:", e);
            tagsContainer.innerHTML = '<span class="tag">Í∏∞Ïà†Ïä§ÌÉù Ï†ïÎ≥¥ ÏóÜÏùå</span>';
        }
    } else if (tagsContainer) {
        tagsContainer.innerHTML = '<span class="tag">Í∏∞Ïà†Ïä§ÌÉù Ï†ïÎ≥¥ ÏóÜÏùå</span>';
    }
};

/**
 * Í∞úÎ∞úÏûê ÏÑ†Ìò∏ Ïú†Ìòï ÌÉúÍ∑∏ ÎèôÏ†Å ÏÉùÏÑ±
 */
scwin.updateDeveloperTypeTags = function() {
    var developerTypesJson = dmp_postDetail.get("preferredDeveloperTypes");

    if (developerTypesJson) {
        try {
            var developerTypes = JSON.parse(developerTypesJson);
            var techTagsContainer = document.querySelector('.tech-tags');

            if (techTagsContainer && developerTypes && developerTypes.length > 0) {
                var existingHtml = techTagsContainer.innerHTML;
                var additionalTags = '';

                for (var i = 0; i < developerTypes.length; i++) {
                    additionalTags += '<span class="tag dev-type-tag">' + developerTypes[i] + '</span>';
                }

                techTagsContainer.innerHTML = existingHtml + additionalTags;
                console.log("Í∞úÎ∞úÏûê Ïú†Ìòï ÌÉúÍ∑∏ Ï∂îÍ∞Ä ÏôÑÎ£å:", developerTypes.length + "Í∞ú");
            }
        } catch (e) {
            console.error("Í∞úÎ∞úÏûê Ïú†Ìòï JSON ÌååÏã± Ïò§Î•ò:", e);
        }
    }
};

/**
 * Í∏∞ÌÉÄ Ï†ïÎ≥¥ ÌÉúÍ∑∏ ÏóÖÎç∞Ïù¥Ìä∏
 */
scwin.updateInfoTags = function() {
    var experienceLevel = scwin.getExperienceLevelText(dmp_postDetail.get("experienceLevel"));
    var location = dmp_postDetail.get("location") || "ÎØ∏Ï†ï";
    var workType = scwin.getWorkTypeText(dmp_postDetail.get("workType"));
    var salaryRange = scwin.getSalaryRangeText(dmp_postDetail.get("salaryRange"));

    // Í∏∞ÌÉÄ Ï†ïÎ≥¥ Ïª®ÌÖåÏù¥ÎÑà Ï∞æÍ∏∞
    var infoContainer = document.querySelector('.info-tags');
    if (infoContainer) {
        var infoHtml = '';
        infoHtml += '<div class="info-tag-item"><span class="info-tag-label">Í≤ΩÎ†•</span><span class="info-tag-value">' + experienceLevel + '</span></div>';
        infoHtml += '<div class="info-tag-item"><span class="info-tag-label">ÏßÄÏó≠</span><span class="info-tag-value">' + location + '</span></div>';
        infoHtml += '<div class="info-tag-item"><span class="info-tag-label">Í∑ºÎ¨¥ÌòïÌÉú</span><span class="info-tag-value">' + workType + '</span></div>';
        infoHtml += '<div class="info-tag-item"><span class="info-tag-label">Í∏âÏó¨</span><span class="info-tag-value">' + salaryRange + '</span></div>';

        infoContainer.innerHTML = infoHtml;
    }
};

/**
 * Î™©Î°ùÏúºÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
 */
scwin.goBackToList = function() {
    try {
        if ($p.parent() && $p.parent().pf_main) {
            $p.parent().pf_main.setSrc("PostList.xml");
        } else {
            $p.url("PostList.xml");
        }
    } catch (e) {
        console.error("ÌéòÏù¥ÏßÄ Ïù¥Îèô Ï§ë Ïò§Î•ò:", e);
        $p.url("PostList.xml");
    }
};

