    }

    console.log("Ï°∞ÌöåÎêú Í≥µÍ≥† Ï†ïÎ≥¥:");
    console.log("- Í≥µÍ≥† ID:", elData.jobPostingId);
    console.log("- Ï†úÎ™©:", elData.title);
    console.log("- ÌöåÏÇ¨Î™Ö:", elData.companyName);
    console.log("- ÏÉÅÌÉú:", elData.status);

    // ÎπÑÌôúÏÑ±ÌôîÎêú Í≥µÍ≥† Ï≤¥ÌÅ¨
    if (elData.status === 'inactive' || elData.status === 'deleted') {
        console.warn("ÎπÑÌôúÏÑ±ÌôîÎêú Í≥µÍ≥†ÏûÖÎãàÎã§.");
        alert("Ïù¥ Í≥µÍ≥†Îäî ÌòÑÏû¨ Î™®ÏßëÏù¥ Ï§ëÎã®ÎêòÏóàÏäµÎãàÎã§.");

        setTimeout(function() {
            scwin.goBackToList();
        }, 1000);
        return false;
    }

    try {
        // Îç∞Ïù¥ÌÑ∞Î•º DataMapÏóê ÏÑ§Ï†ï
        dmp_postDetail.setJSON(elData);

        // ÌôîÎ©¥ ÏóÖÎç∞Ïù¥Ìä∏
        scwin.updateUI();

        console.log("‚úÖ Í≥µÍ≥† ÏÉÅÏÑ∏ Ï†ïÎ≥¥ Î∞îÏù∏Îî© ÏôÑÎ£å");

        // ÏÑ±Í≥µÏ†ÅÏúºÎ°ú Î°úÎìúÎêòÏóàÏùåÏùÑ ÌëúÏãú
