scwin.updateUI = function() {
    console.log("=== UI ÏóÖÎç∞Ïù¥Ìä∏ ÏãúÏûë ===");

    // Í∏∞Ïà†Ïä§ÌÉù ÌÉúÍ∑∏ ÎèôÏ†Å ÏÉùÏÑ±
