scwin.updateJobMeta = function() {
    var companyName = dmp_postDetail.get("companyName") || "Ï†ïÎ≥¥ÏóÜÏùå";
    var experienceLevel = scwin.getExperienceLevelText(dmp_postDetail.get("experienceLevel"));
    var location = dmp_postDetail.get("location") || "Ï†ïÎ≥¥ÏóÜÏùå";

    var metaText = companyName + " ‚Ä¢ " + experienceLevel + " ‚Ä¢ " + location + " Îì± Í∑ºÎ¨¥";

    // Î©îÌÉÄ Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    var metaElement = document.querySelector('.job-meta');
    if (metaElement && metaElement.querySelector('w2\\:textbox')) {
        metaElement.querySelector('w2\\:textbox').textContent = metaText;
    }
};

