        scwin.goBackToList();
    }
};

// Í≥µÏú†ÌïòÍ∏∞
scwin.btnShare_onclick = function(e) {
    var title = dmp_postDetail.get("title") || "Ï±ÑÏö©Í≥µÍ≥†";
    var jobPostingId = dmp_postDetail.get("jobPostingId");

    if (navigator.share) {
        navigator.share({
            title: title,
            text: title + " - Ï±ÑÏö©Í≥µÍ≥†",
            url: window.location.href
        });
    } else {
        // Í≥µÏú† API ÎØ∏ÏßÄÏõê Ïãú ÌÅ¥Î¶ΩÎ≥¥Îìú Î≥µÏÇ¨
        navigator.clipboard.writeText(window.location.href).then(function() {
            alert("ÎßÅÌÅ¨Í∞Ä ÌÅ¥Î¶ΩÎ≥¥ÎìúÏóê Î≥µÏÇ¨ÎêòÏóàÏäµÎãàÎã§.");
        }).catch(function() {
            // ÌÅ¥Î¶ΩÎ≥¥Îìú Î≥µÏÇ¨ Ïã§Ìå® Ïãú ÏßÅÏ†ë ÌëúÏãú
            prompt("ÏïÑÎûò ÎßÅÌÅ¨Î•º Î≥µÏÇ¨ÌïòÏÑ∏Ïöî:", window.location.href);
        });
    }
};

