/**
 * Í≤ΩÎ†•ÏàòÏ§Ä ÌïúÍ∏Ä Î≥ÄÌôò
 */
scwin.getExperienceLevelText = function(experienceLevel) {
    switch(experienceLevel) {
        case 'any': return 'Í≤ΩÎ†•Î¨¥Í¥Ä';
        case 'junior': return 'Ïã†ÏûÖ¬∑1-3ÎÖÑ';
        case 'senior': return 'ÏãúÎãàÏñ¥¬∑3ÎÖÑ+';
        default: return experienceLevel || 'Ï†ïÎ≥¥ÏóÜÏùå';
    }
};

/**
 * Í∑ºÎ¨¥ÌòïÌÉú ÌïúÍ∏Ä Î≥ÄÌôò
 */
scwin.getWorkTypeText = function(workType) {
    switch(workType) {
        case 'remote': return 'ÏõêÍ≤©Í∑ºÎ¨¥';
        case 'onsite': return 'Ï∂úÍ∑ºÍ∑ºÎ¨¥';
        case 'hybrid': return 'ÌïòÏù¥Î∏åÎ¶¨Îìú';
        default: return workType || 'Ï†ïÎ≥¥ÏóÜÏùå';
    }
};

/**
 * Í∏âÏó¨Î≤îÏúÑ ÌïúÍ∏Ä Î≥ÄÌôò
 */
scwin.getSalaryRangeText = function(salaryRange) {
    switch(salaryRange) {
        case 'under25': return '~2,500ÎßåÏõê';
        case '25to35': return '2,500~3,500ÎßåÏõê';
        case '35to50': return '3,500~5,000ÎßåÏõê';
        case 'over50': return '5,000ÎßåÏõê+';
        default: return salaryRange || 'ÌòëÏùò';
    }
};
