head.script:
scwin.onpageload = function () {
    scwin.init();
};

// 초기화 함수
scwin.init = function () {
    // 검색 조건 초기화
    dmp_searchParam.set("pageIndex", 1);
    dmp_searchParam.set("pageSize", 10);

    // cookie에서 accountId 가져오기
    scwin.userInfo = $p.parent().dma_userInfoVo.getJSON();
    if (scwin.userInfo && scwin.userInfo.accountId) {
        dmp_accountIdVo.set("accountId", scwin.userInfo.accountId);
        dmp_searchParam.set("userId", scwin.userInfo.accountId);
    }

    // 지원현황 목록 조회
    $c.sbm.execute($p, sbm_applicationHistoryList);

    // 통계 데이터 조회
    $c.sbm.execute($p, sbm_applicationStats);

    // 초기 통계 카드 설정
    // scwin.initializeStatCards();
};

// pageFrame에서 즉시 실행
scwin.init();

// 지원현황 목록 조회 완료
scwin.sbm_applicationHistoryList_submitdone = function (e) {
    try {
        var res = e.responseJSON;
        console.log("=== 전체 응답 데이터 ===", res);

        if (res && res.applicationListVoList) {
            console.log("=== applicationListVoList 데이터 ===", res.applicationListVoList);
            console.log("=== 배열 길이 ===", res.applicationListVoList.length);

            dlt_applicationListVo.setJSON(res.applicationListVoList);
            console.log("=== dataList 설정 완료 ===");
            console.log("=== dataList 행 수 ===", dlt_applicationListVo.getRowCount());

            // 수동으로 generator에 데이터 바인딩
            scwin.bindApplicationData();

            // 빈 상태 표시/숨김 처리
            var emptyState = document.getElementById('empty_state');
            if (res.applicationListVoList.length === 0) {
                if (emptyState) emptyState.style.display = 'block';
            } else {
                if (emptyState) emptyState.style.display = 'none';
            }
        } else {
            console.log("=== applicationListVoList가 없습니다 ===");
            console.log("=== res 구조 ===", res);
        }

        // UI 업데이트
        scwin.applyStatusClasses();
    } catch (err) {
        console.error("지원현황 조회 오류:", err);
        alert("지원현황 조회 중 오류가 발생했습니다.");
    }
};

// 지원현황 데이터를 generator에 바인딩하는 함수
scwin.bindApplicationData = function() {
    // 기존 내용 제거
    gen_applications.removeAll();

    // DataList에서 데이터 가져오기
    var rowCount = dlt_applicationListVo.getRowCount();

    if (rowCount === 0) {
        console.log("지원현황 데이터가 없습니다.");
        return;
    }

    for (var i = 0; i < rowCount; i++) {
        // 새로운 아이템 추가
        gen_applications.insertChild();

        // 데이터 가져오기
        var name = dlt_applicationListVo.getCellData(i, "name") || "";
        var title = dlt_applicationListVo.getCellData(i, "title") || "";
        var experienceLevel = dlt_applicationListVo.getCellData(i, "experienceLevel") || "";
        var jobPostingId = dlt_applicationListVo.getCellData(i, "jobPostingId") || "";

        // 각 컴포넌트에 값 설정
        var txt_company_name = gen_applications.getChild(i, "txt_company_name");
        var txt_job_title = gen_applications.getChild(i, "txt_job_title");
        var txt_experience_level = gen_applications.getChild(i, "txt_experience_level");
        var txt_job_posting_id = gen_applications.getChild(i, "txt_job_posting_id");

        if (txt_company_name) txt_company_name.setValue(name);
        if (txt_job_title) txt_job_title.setValue(title);
        if (txt_experience_level) txt_experience_level.setValue(experienceLevel);
        if (txt_job_posting_id) txt_job_posting_id.setValue(jobPostingId);

        console.log("바인딩 완료 - 행:", i, "회사명:", name, "공고명:", title);
    }
};

// 지원현황 목록 조회 오류
scwin.sbm_applicationHistoryList_submiterror = function (e) {
    console.error("지원현황 조회 실패:", e);
    alert("지원현황을 불러오는데 실패했습니다.");
};

// 통계 데이터로부터 UI 업데이트
scwin.updateStatsFromData = function () {
    var totalCount = 0;
    var waitingCount = 0;
    var acceptedCount = 0;
    var rejectedCount = 0;

    // 통계 데이터가 있는 경우 사용
    var statsCount = dlt_applicationStatsVo.getRowCount();
    if (statsCount > 0) {
        for (var i = 0; i < statsCount; i++) {
            var statusType = dlt_applicationStatsVo.getCellData(i, "statusType");
            var count = dlt_applicationStatsVo.getCellData(i, "statusCount");
            if(statusType === "TOTAL"){
                totalCount = count;
            } else if  (statusType === "PENDING") {
                waitingCount = count;
            } else if (statusType === "ACCEPTED") {
                acceptedCount = count;
            } else if (statusType === "REJECTED") {
                rejectedCount = count;
            }
        }
    }

    // 통계 UI 업데이트
    setTimeout(function () {
        var totalElement = document.getElementById('totalApplications');
        var waitingElement = document.getElementById('waitingApplications');
        var acceptedElement = document.getElementById('acceptedApplications');
        var rejectedElement = document.getElementById('rejectedApplications');

        if (totalElement) totalElement.textContent = totalCount;
        if (waitingElement) waitingElement.textContent = waitingCount;
        if (acceptedElement) acceptedElement.textContent = acceptedCount;
        if (rejectedElement) rejectedElement.textContent = rejectedCount;
    }, 100);
};

// 통계 카드 선택 및 필터링
scwin.selectStatCard = function (cardElement, filterType) {
    // 모든 통계 카드에서 active 클래스 제거
    total_applications.removeClass('active');
    in_progress_applications.removeClass('active');
    accepted_applications.removeClass('active');
    rejected_applications.removeClass('active');

    // 클릭된 카드에 active 클래스 추가
    cardElement.addClass('active');

    // 해당 필터 적용
    scwin.filterApplications(filterType);
};

// 초기 통계 카드 설정
scwin.initializeStatCards = function () {
    setTimeout(function () {
        var firstStatCard = document.querySelector('.stat-card');
        if (firstStatCard) {
            firstStatCard.addClass('active');
        }
    }, 100);
};

// 필터링
scwin.filterApplications = function (status) {
    // 검색 조건 설정
    if (status !== '') {
        dmp_searchParam.set("applicationStatus", status);
    } else {
        dmp_searchParam.set("applicationStatus", '');
    }

    // 지원현황 목록 재조회
    $c.sbm.execute($p, sbm_applicationHistoryList);
};

// 상태별 클래스 적용
scwin.applyStatusClasses = function () {
    var totalCount = dlt_applicationListVo.getRowCount();

    setTimeout(function () {
        var cards = document.querySelectorAll('.application-card');
        var statusBadges = document.querySelectorAll('.status-badge');

        for (var i = 0; i < cards.length && i < totalCount; i++) {
            var experienceLevel = dlt_applicationListVo.getCellData(i, "experienceLevel");
            var card = cards[i];
            var badge = statusBadges[i];

            if (experienceLevel === "신입" || experienceLevel === "경력무관") {
                card.classList.add('pending');
                if (badge) badge.className = 'status-badge status-pending';
            } else if (experienceLevel === "경력") {
                card.classList.add('accepted');
                if (badge) badge.className = 'status-badge status-accepted';
            } else {
                card.classList.add('rejected');
                if (badge) badge.className = 'status-badge status-rejected';
            }
        }
    }, 100);
};

// 지원현황 상세보기
scwin.showApplicationDetail = function (rowIndex) {
    var name = dlt_applicationListVo.getCellData(rowIndex, "name");
    var title = dlt_applicationListVo.getCellData(rowIndex, "title");
    var experienceLevel = dlt_applicationListVo.getCellData(rowIndex, "experienceLevel");
    var preferredDeveloperTypes = dlt_applicationListVo.getCellData(rowIndex, "preferredDeveloperTypes");
    var jobPostingId = dlt_applicationListVo.getCellData(rowIndex, "jobPostingId");

    var detailMessage = "=== 지원 상세 정보 ===\n\n";
    detailMessage += "회사명: " + name + "\n";
    detailMessage += "공고명: " + title + "\n";
    detailMessage += "경력요구사항: " + experienceLevel + "\n";
    detailMessage += "선호 개발자 유형: " + preferredDeveloperTypes + "\n";
    detailMessage += "\n해당 공고 페이지로 이동하시겠습니까?";

    if (confirm(detailMessage)) {
        alert("공고 ID " + jobPostingId + " 페이지로 이동합니다.");
    }
};

// 지원현황 행 클릭 이벤트 핸들러
scwin.handleApplicationRowClick = function(e) {
    var index = this.getGeneratedIndex();
    if (typeof index === 'number' && index >= 0) {
        scwin.showApplicationDetail(index);
    } else {
        console.error("유효한 인덱스를 가져올 수 없습니다.");
    }
};

scwin.total_applications_onclick = function (e) {
    scwin.selectStatCard(this, '');
};

scwin.in_progress_applications_onclick = function (e) {
    scwin.selectStatCard(this, '서류검토');
};

scwin.accepted_applications_onclick = function (e) {
    scwin.selectStatCard(this, '서류합격');
};

scwin.rejected_applications_onclick = function (e) {
    scwin.selectStatCard(this, '불합격');
};

scwin.sbm_applicationStats_submitdone = function (e) {
    try {
        var res = e.responseJSON;

        if (res && res.elData && res.elData.applicationStatusVo) {
            dlt_applicationStatsVo.setJSON(res.elData.applicationStatusVo);

            // 5. UI 업데이트 함수 호출
            scwin.updateStatsFromData();

        } else {
            console.warn("응답 데이터 구조가 예상과 다릅니다:", res);
        }

    } catch (err) {
        console.error("통계 데이터 처리 오류:", err);
        alert("통계 데이터를 처리하는 중 오류가 발생했습니다.");
    }
};
