// ì—…ë¡œë“œëœ íŒŒì¼ë“¤ì„ ì €ì¥í•  ì „ì—­ ë³€ìˆ˜
scwin.uploadedFiles = [];

scwin.onpageload = function() {
    console.log("ì½”ë“œë¶„ì„ ë©”ì¸ í˜ì´ì§€ ë¡œë“œë¨");

    // ì‚¬ìš©ì ì¸ì¦ ì •ë³´ í™•ì¸ ë° ì„¤ì •
    scwin.initializeUserInfo();

    // ë©€í‹° íŒŒì¼ ì—…ë¡œë“œ ì´ë²¤íŠ¸ ì„¤ì •
    scwin.initializeMultiFileUpload();
