// ë‹¤ìŒ ë²„íŠ¼ í´ë¦­
scwin.btn_next_onclick = function() {
    // ìµœì†Œ í•˜ë‚˜ì˜ íŒŒì¼ì´ ì—…ë¡œë“œë˜ì—ˆëŠ”ì§€ í™•ì¸
    if (scwin.uploadedFiles.length === 0) {
        alert('ìµœì†Œ í•˜ë‚˜ ì´ìƒì˜ íŒŒì¼ì„ ì—…ë¡œë“œí•´ì£¼ì„¸ìš”.');
        return;
    }

    // ë¡œë”© í‘œì‹œ
    scwin.showLoading(true);

    // ì½”ë“œ ë¶„ì„ API í˜¸ì¶œ
    var requestData = {
        userId: 'TEST_USER', // ì‹¤ì œë¡œëŠ” ë¡œê·¸ì¸ëœ ì‚¬ìš©ì ID ì‚¬ìš©
        modelFile: dma_codeFiles.get('modelFile') || '',
        controllerFile: dma_codeFiles.get('controllerFile') || '',
        serviceFile: dma_codeFiles.get('serviceFile') || '',
        repositoryFile: dma_codeFiles.get('repositoryFile') || '',
        modelFileName: dma_codeFiles.get('modelFileName') || '',
        controllerFileName: dma_codeFiles.get('controllerFileName') || '',
        serviceFileName: dma_codeFiles.get('serviceFileName') || '',
        repositoryFileName: dma_codeFiles.get('repositoryFileName') || ''
    };

    // ìš”ì²­ ë°ì´í„°ë¥¼ DataMapì— ì„¤ì •
    for (var key in requestData) {
        dma_codeFiles.set(key, requestData[key]);
    }

    // Manual AJAX í˜¸ì¶œë¡œ wrapper ë¬¸ì œ í•´ê²°
    scwin.manualSubmission(requestData);
};

// Manual AJAX í˜¸ì¶œ í•¨ìˆ˜ (XMLHttpRequest ì‚¬ìš©)
scwin.manualSubmission = function(requestData) {
    var xhr = new XMLHttpRequest();
    xhr.open('POST', '/InsWebApp/CA0001Analyze.pwkjson', true);
    xhr.setRequestHeader('Content-Type', 'application/json');

    xhr.onreadystatechange = function() {
        if (xhr.readyState === 4) {
            if (xhr.status === 200) {
                try {
                    var response = JSON.parse(xhr.responseText);
                    scwin.handleSubmissionSuccess(response);
                } catch (e) {
                    console.error('JSON íŒŒì‹± ì˜¤ë¥˜:', e);
                    scwin.handleSubmissionError(xhr, 'parsererror', 'JSON íŒŒì‹± ì‹¤íŒ¨');
                }
            } else {
                scwin.handleSubmissionError(xhr, 'error', 'HTTP ' + xhr.status);
            }
        }
    };

    xhr.send(JSON.stringify(requestData));
};

// ì„±ê³µ í•¸ë“¤ëŸ¬
scwin.handleSubmissionSuccess = function(response) {
    scwin.showLoading(false);
    console.log('=== ì½”ë“œ ë¶„ì„ ì‘ë‹µ (Manual AJAX) ===');
    console.log('ì‘ë‹µ ë°ì´í„°:', response);

    // ProWorks5 ì˜¤ë¥˜ ì‘ë‹µ ì²´í¬
    if (response && response.elHeader) {
        console.log('ProWorks5 í”„ë ˆì„ì›Œí¬ ì‘ë‹µ ê°ì§€');
        console.log('elHeader:', response.elHeader);

        var header = response.elHeader;
        if (header.resSuc === false || header.resCode) {
            console.error('ì„œë²„ ì˜¤ë¥˜ ë°œìƒ:', header.resMsg);
            alert('ì„œë²„ì—ì„œ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: ' + header.resMsg + 
                  '\nì˜¤ë¥˜ ì½”ë“œ: ' + header.resCode + 
                  '\nì„œë²„ ë¡œê·¸ë¥¼ í™•ì¸í•˜ê±°ë‚˜ ê´€ë¦¬ìì—ê²Œ ë¬¸ì˜í•˜ì„¸ìš”.');
            return;
        }
    }

    // ì •ìƒì ì¸ ì‘ë‹µ ì²´í¬
    if (response && response.typeCode && response.typeCode.trim() !== '') {
        // ì„±ê³µ ì²˜ë¦¬
        scwin.displayAnalysisResult(response);
    } else if (response && response.success !== false) {
        // ì„±ê³µ ì²˜ë¦¬ (legacy)
        scwin.displayAnalysisResult(response);
    } else {
        // ì˜¤ë¥˜ ì²˜ë¦¬
        var errorMsg = response ? response.message : 'ì•Œ ìˆ˜ ì—†ëŠ” ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.';
        alert('ì½”ë“œ ë¶„ì„ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤: ' + errorMsg);
    }
};

// ì˜¤ë¥˜ í•¸ë“¤ëŸ¬
scwin.handleSubmissionError = function(xhr, status, error) {
    scwin.showLoading(false);
    console.error('=== AJAX ì˜¤ë¥˜ ===');
    console.error('Status:', status);
    console.error('Error:', error);
    console.error('Response:', xhr.responseText);

    alert('ì„œë²„ í†µì‹  ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: ' + error);
};

// ë¶„ì„ ê²°ê³¼ í‘œì‹œ
scwin.displayAnalysisResult = function(result) {
    console.log('ë¶„ì„ ê²°ê³¼ í‘œì‹œ:', result);

    // ë¶„ì„ ê²°ê³¼ë¥¼ ì„¸ì…˜ì— ì €ì¥
    if (result && (result.typeCode || result.codeAnalysisResultVo)) {
        // ì‹¤ì œ ë¶„ì„ ê²°ê³¼ê°€ ìˆëŠ” ê²½ìš°
        var analysisData = result.codeAnalysisResultVo || result;
        sessionStorage.setItem('analysisResult', JSON.stringify(analysisData));

        // ì„±ê³µ ì•Œë¦¼ í›„ ê²°ê³¼ í˜ì´ì§€ë¡œ ì´ë™
        alert('ë¶„ì„ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤.');
    $p.url("CodeAnalysisResult.xml");
    } else {
        // ê²°ê³¼ê°€ ì—†ëŠ” ê²½ìš°ì—ë„ ì™„ë£Œ ë©”ì‹œì§€ í‘œì‹œ
        alert('ë¶„ì„ì´ ì™„ë£Œë˜ì—ˆìŠµë‹ˆë‹¤.');
    }
};

// ì½”ë“œ ë¶„ì„ ì„±ê³µ ì½œë°±
scwin.sbm_codeAnalyze_submitdone = function(e) {
    scwin.showLoading(false);
    console.log('=== ì½”ë“œ ë¶„ì„ ì‘ë‹µ ë””ë²„ê¹… ì •ë³´ ===');
    console.log('ì „ì²´ ì‘ë‹µ ê°ì²´:', e);
    console.log('ì‘ë‹µ ìƒíƒœ ì½”ë“œ:', e.responseStatusCode);
    console.log('ì‘ë‹µ í—¤ë”:', e.responseHeaders);
    console.log('ì‘ë‹µ ë°ì´í„° íƒ€ì…:', typeof e.responseJSON);
    console.log('ì‘ë‹µ JSON ë°ì´í„°:', e.responseJSON);
    console.log('ì‘ë‹µ í…ìŠ¤íŠ¸ ì›ë³¸:', e.responseText);

    var response = e.responseJSON;

    // ì‘ë‹µì´ nullì´ê±°ë‚˜ undefinedì¸ ê²½ìš° ì²˜ë¦¬
    if (!response) {
        console.error('ì‘ë‹µ ë°ì´í„°ê°€ null ë˜ëŠ” undefinedì…ë‹ˆë‹¤.');
        console.log('ì›ë³¸ ì‘ë‹µ í…ìŠ¤íŠ¸ë¡œ JSON íŒŒì‹± ì‹œë„:', e.responseText);

        if (e.responseText) {
            try {
                response = JSON.parse(e.responseText);
                console.log('ìˆ˜ë™ íŒŒì‹± ì„±ê³µ:', response);
            } catch (parseError) {
                console.error('JSON íŒŒì‹± ì‹¤íŒ¨:', parseError);
                alert('ì„œë²„ ì‘ë‹µ íŒŒì‹±ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤. ê´€ë¦¬ìì—ê²Œ ë¬¸ì˜í•˜ì„¸ìš”.');
                return;
            }
        } else {
            alert('ì„œë²„ì—ì„œ ë¹ˆ ì‘ë‹µì„ ë°›ì•˜ìŠµë‹ˆë‹¤. ê´€ë¦¬ìì—ê²Œ ë¬¸ì˜í•˜ì„¸ìš”.');
            return;
        }
    }

    console.log('ìµœì¢… response ê°ì²´:', response);

    // WebSquare í”„ë ˆì„ì›Œí¬ ì˜¤ë¥˜ ì‘ë‹µ ì²˜ë¦¬
    if (response.elHeader) {
        console.log('WebSquare í”„ë ˆì„ì›Œí¬ ì‘ë‹µ ê°ì§€');
        console.log('elHeader:', response.elHeader);

        var header = response.elHeader;
        if (header.resSuc === false || header.resCode === 'ERROR.SYS.002') {
            console.error('ì„œë²„ ì˜¤ë¥˜ ë°œìƒ:', header.resMsg);
            alert('ì„œë²„ì—ì„œ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤: ' + header.resMsg + 
                  '\nì˜¤ë¥˜ ì½”ë“œ: ' + header.resCode + 
                  '\nì„œë²„ ë¡œê·¸ë¥¼ í™•ì¸í•˜ê±°ë‚˜ ê´€ë¦¬ìì—ê²Œ ë¬¸ì˜í•˜ì„¸ìš”.');
            return;
        }
    }

    console.log('typeCode ê°’:', response.typeCode);
    console.log('typeCode íƒ€ì…:', typeof response.typeCode);

    // typeCode ê²€ì¦ ê°•í™”
    if (response && response.typeCode && response.typeCode.trim() !== '') {
        console.log('ë¶„ì„ ì„±ê³µ - typeCode:', response.typeCode);

        // ë¶„ì„ ê²°ê³¼ë¥¼ ì„¸ì…˜ì— ì €ì¥
        sessionStorage.setItem('analysisResult', JSON.stringify(response));

        // ê²°ê³¼ë¥¼ ê°™ì€ í˜ì´ì§€ì—ì„œ í‘œì‹œ
        scwin.displayAnalysisResult(response);
    } else {
        console.error('typeCodeê°€ ì—†ê±°ë‚˜ ë¹ˆ ê°’ì…ë‹ˆë‹¤.');
        console.log('ì‘ë‹µ ê°ì²´ì˜ ëª¨ë“  í‚¤:', Object.keys(response || {}));

        // ì‘ë‹µì— ì˜¤ë¥˜ ë©”ì‹œì§€ê°€ ìˆëŠ”ì§€ í™•ì¸
        if (response && response.error) {
            alert('ì½”ë“œ ë¶„ì„ ì‹¤íŒ¨: ' + response.error);
        } else if (response && response.message) {
            alert('ì½”ë“œ ë¶„ì„ ì‹¤íŒ¨: ' + response.message);
        } else if (response && response.elHeader && response.elHeader.resMsg) {
            alert('ì„œë²„ ì˜¤ë¥˜: ' + response.elHeader.resMsg);
        } else {
            alert('ì½”ë“œ ë¶„ì„ì— ì‹¤íŒ¨í–ˆìŠµë‹ˆë‹¤. ì½˜ì†” ë¡œê·¸ë¥¼ í™•ì¸í•˜ì„¸ìš”.');
        }
    }
};

// ì½”ë“œ ë¶„ì„ ì‹¤íŒ¨ ì½œë°±
scwin.sbm_codeAnalyze_submiterror = function(e) {
    scwin.showLoading(false);
    console.error('ì½”ë“œ ë¶„ì„ API í˜¸ì¶œ ì‹¤íŒ¨:', e);

    var errorMessage = 'ì½”ë“œ ë¶„ì„ ì¤‘ ì˜¤ë¥˜ê°€ ë°œìƒí–ˆìŠµë‹ˆë‹¤.';
    if (e.responseJSON && e.responseJSON.message) {
        errorMessage += '\nì˜¤ë¥˜: ' + e.responseJSON.message;
    } else if (e.responseText) {
        errorMessage += '\nì˜¤ë¥˜: ' + e.responseText;
    }

    alert(errorMessage);
};

// ë¡œë”© í‘œì‹œ/ìˆ¨ê¹€
scwin.showLoading = function(show) {
    if (show) {
        // ë¡œë”© ì˜¤ë²„ë ˆì´ í‘œì‹œ
        var loadingHtml = '<div id="loadingOverlay" style="position: fixed; top: 0; left: 0; width: 100%; height: 100%; background: rgba(0,0,0,0.5); z-index: 9999; display: flex; align-items: center; justify-content: center;">' +
                         '<div style="background: white; padding: 30px; border-radius: 10px; text-align: center;">' +
                         '<div style="font-size: 18px; margin-bottom: 15px;">ì½”ë“œ ë¶„ì„ ì¤‘...</div>' +
                         '<div style="width: 50px; height: 50px; border: 5px solid #f3f3f3; border-top: 5px solid #007bff; border-radius: 50%; animation: spin 1s linear infinite; margin: 0 auto;"></div>' +
                         '</div></div>';
        $('body').append(loadingHtml);

        // ìŠ¤í”¼ë„ˆ ì• ë‹ˆë©”ì´ì…˜ CSS ì¶”ê°€
        if (!$('#spinnerStyle').length) {
            $('head').append('<style id="spinnerStyle">@keyframes spin { 0% { transform: rotate(0deg); } 100% { transform: rotate(360deg); }}</style>');
        }
    } else {
        $('#loadingOverlay').remove();
    }
};

// ë¶„ì„ ê²°ê³¼ í‘œì‹œ
scwin.displayAnalysisResult = function(data) {
    var typeDefinitions = {
        'AI': { name: 'í˜ì‹ ì  ì•„í‚¤í…íŠ¸', desc: 'ìƒˆë¡œìš´ ê¸°ìˆ ì„ í™œìš©í•˜ì—¬ ì²´ê³„ì ì¸ ì‹œìŠ¤í…œì„ êµ¬ì¶•í•˜ëŠ” ê°œë°œì' },
        'AR': { name: 'ê°œì„ í˜• ì•„í‚¤í…íŠ¸', desc: 'ê¸°ì¡´ ì½”ë“œë¥¼ ì²´ê³„ì ìœ¼ë¡œ ê°œì„ í•˜ê³  ìµœì í™”í•˜ëŠ” ê°œë°œì' },
        'BI': { name: 'í˜ì‹ ì  ë¹Œë”', desc: 'ìƒˆë¡œìš´ ê¸°ìˆ ë¡œ ë¹ ë¥´ê³  ì‹¤ìš©ì ì¸ ì†”ë£¨ì…˜ì„ ë§Œë“œëŠ” ê°œë°œì' },
        'BR': { name: 'ì‹¤ìš©ì  ë¹Œë”', desc: 'ê¸°ì¡´ ë°©ì‹ì„ ê°œì„ í•˜ì—¬ íš¨ìœ¨ì ì¸ ì†”ë£¨ì…˜ì„ êµ¬ì¶•í•˜ëŠ” ê°œë°œì' }
    };

    var typeCode = data.typeCode || 'AR';
    var typeInfo = typeDefinitions[typeCode] || typeDefinitions['AR'];

    // ì—…ë¡œë“œ ì„¹ì…˜ ìˆ¨ê¸°ê¸°
    $('.upload-section').hide();

    // ê²°ê³¼ ì„¹ì…˜ ë³´ì´ê¸°
    $('.result-section').show();

    // ê²°ê³¼ ë°ì´í„° ì„¤ì •
    $('.result-type-code').text(typeCode);
    $('.result-type-name').text(typeInfo.name);
    $('.result-type-desc').text(typeInfo.desc);

    // ìƒˆë¡œìš´ í†µí•© ì ìˆ˜ ì²´ê³„ ì ìš©
    var devStyleScore = data.developmentStyleScore || 0;
    var devPrefScore = data.developerPreferenceScore || 0;
    var confidenceScore = data.confidenceScore || 0;

    // ê°œë°œ ìŠ¤íƒ€ì¼ ì ìˆ˜ í‘œì‹œ (-50~+50 â†’ ì ˆëŒ“ê°’ìœ¼ë¡œ í‘œì‹œ)
    $('.score-development-style').text(Math.abs(devStyleScore) + 'ì ');
    $('.development-style-type').text(devStyleScore > 0 ? 'Architect ìš°ì„¸' : 'Builder ìš°ì„¸');

    // ê°œë°œì ì„ í˜¸ë„ ì ìˆ˜ í‘œì‹œ (-50~+50 â†’ ì ˆëŒ“ê°’ìœ¼ë¡œ í‘œì‹œ)  
    $('.score-developer-preference').text(Math.abs(devPrefScore) + 'ì ');
    $('.developer-preference-type').text(devPrefScore > 0 ? 'Refactor ìš°ì„¸' : 'Innovator ìš°ì„¸');

    // ì‹ ë¢°ë„ í‘œì‹œ
    $('.score-confidence').text(Math.round(confidenceScore * 100) + '%');

    // ê²°ê³¼ ì–¸ì–´ í‘œì‹œ
    $('.result-language').text('Detected Language: ' + (data.language || 'Unknown'));

    console.log('ê²°ê³¼ í‘œì‹œ ì™„ë£Œ:', typeCode, typeInfo.name);
};

