head.script:
scwin.onpageload = function() {
    // Ï¥àÍ∏∞Ìôî
    dma_location.set("selectedLocations", "");
    scwin.selectedLocationsArray = []; // ÏÑ†ÌÉùÎêú ÏßÄÏó≠ Î∞∞Ïó¥
    scwin.maxSelections = 5; // ÏµúÎåÄ ÏÑ†ÌÉù Í∞ÄÎä• Í∞úÏàò
    scwin.updateNextButton();
    scwin.updateSelectedLocationsDisplay();
};

// ÏßÄÏó≠ ÏòµÏÖò ÏÑ†ÌÉù/Ìï¥Ï†ú
scwin.selectLocation = function(locationValue) {
    var clickedElement = event.target.closest('.location-option');

    // ÎπÑÌôúÏÑ±ÌôîÎêú Ìï≠Î™©Ïù¥Î©¥ Î¨¥Ïãú
    if (clickedElement.classList.contains('disabled')) {
        return;
    }

    if (clickedElement.classList.contains('selected')) {
        // Ïù¥ÎØ∏ ÏÑ†ÌÉùÎêú Í≤ΩÏö∞ - Ìï¥Ï†ú
        clickedElement.classList.remove('selected');
        scwin.removeFromSelectedLocations(locationValue);
    } else {
        // Ï†ÑÏ≤¥ ÏßÄÏó≠ ÏÑ†ÌÉù Î°úÏßÅ
        if (locationValue === 'Ï†ÑÏ≤¥ÏßÄÏó≠') {
            // Ï†ÑÏ≤¥ ÏßÄÏó≠ ÏÑ†ÌÉù Ïãú Îã§Î•∏ Î™®Îì† ÏßÄÏó≠ Ìï¥Ï†ú
            scwin.clearAllLocations();
            clickedElement.classList.add('selected');
            scwin.addToSelectedLocations(locationValue);
        } else {
            // ÌäπÏ†ï ÏßÄÏó≠ ÏÑ†ÌÉù Ïãú
            // 1. Ï†ÑÏ≤¥ ÏßÄÏó≠Ïù¥ ÏÑ†ÌÉùÎêòÏñ¥ ÏûàÏúºÎ©¥ Ìï¥Ï†ú
            if (scwin.selectedLocationsArray.indexOf('Ï†ÑÏ≤¥ÏßÄÏó≠') !== -1) {
                scwin.clearAllLocations();
            }

            // 2. ÏµúÎåÄ Í∞úÏàò ÌôïÏù∏
            if (scwin.selectedLocationsArray.length >= scwin.maxSelections) {
                $c.win.alert("ÏµúÎåÄ " + scwin.maxSelections + "Í∞úÍπåÏßÄÎßå ÏÑ†ÌÉù Í∞ÄÎä•Ìï©ÎãàÎã§.");
                return;
            }

            clickedElement.classList.add('selected');
            scwin.addToSelectedLocations(locationValue);
        }
    }

    scwin.updateSelectedLocationsDisplay();
    scwin.updateLocationOptions();
    scwin.updateNextButton();
};

// Î™®Îì† ÏßÄÏó≠ ÏÑ†ÌÉù Ìï¥Ï†ú
scwin.clearAllLocations = function() {
    scwin.selectedLocationsArray = [];
    dma_location.set("selectedLocations", "");

    // UIÏóêÏÑú Î™®Îì† ÏÑ†ÌÉù Ìï¥Ï†ú
    var locationOptions = document.querySelectorAll('.location-option');
    locationOptions.forEach(function(option) {
        option.classList.remove('selected');
    });
};

// ÏÑ†ÌÉùÎêú ÏßÄÏó≠Ïóê Ï∂îÍ∞Ä
scwin.addToSelectedLocations = function(locationValue) {
    if (scwin.selectedLocationsArray.indexOf(locationValue) === -1) {
        scwin.selectedLocationsArray.push(locationValue);
        dma_location.set("selectedLocations", scwin.selectedLocationsArray.join(','));
    }
};

// ÏÑ†ÌÉùÎêú ÏßÄÏó≠ÏóêÏÑú Ï†úÍ±∞
scwin.removeFromSelectedLocations = function(locationValue) {
    var index = scwin.selectedLocationsArray.indexOf(locationValue);
    if (index > -1) {
        scwin.selectedLocationsArray.splice(index, 1);
        dma_location.set("selectedLocations", scwin.selectedLocationsArray.join(','));
    }
};

// ÌÉúÍ∑∏ ÌÅ¥Î¶≠ÏúºÎ°ú Ï†úÍ±∞
scwin.removeLocationTag = function(locationValue) {
    scwin.removeFromSelectedLocations(locationValue);

    // UIÏóêÏÑú ÏÑ†ÌÉù Ìï¥Ï†ú
    var locationOptions = document.querySelectorAll('.location-option');
    locationOptions.forEach(function(option) {
        if (option.textContent.trim() === locationValue) {
            option.classList.remove('selected');
        }
    });

    scwin.updateSelectedLocationsDisplay();
    scwin.updateLocationOptions();
    scwin.updateNextButton();
};

// ÏÑ†ÌÉùÎêú ÏßÄÏó≠ ÌëúÏãú ÏóÖÎç∞Ïù¥Ìä∏
scwin.updateSelectedLocationsDisplay = function() {
    var container = document.querySelector('.selected-locations-container');
    var countElement = document.querySelector('.selection-count');

    if (scwin.selectedLocationsArray.length === 0) {
        container.innerHTML = '<div class="empty-message">ÏÑ†ÌÉùÎêú ÏßÄÏó≠Ïù¥ ÏóÜÏäµÎãàÎã§ (Ï†ÑÏ≤¥ ÏßÄÏó≠ ÎòêÎäî ÏµúÎåÄ ' + scwin.maxSelections + 'Í∞ú ÏÑ†ÌÉù Í∞ÄÎä•)</div>';
    } else {
        var html = '';
        scwin.selectedLocationsArray.forEach(function(location) {
            html += '<div class="location-tag" onclick="scwin.removeLocationTag(\'' + location + '\')">' +
                   '<span>' + location + '</span>' +
                   '<span class="location-tag-remove">√ó</span>' +
                   '</div>';
        });
        container.innerHTML = html;
    }

    // ÏÑ†ÌÉù Í∞úÏàò ÌëúÏãú
    if (countElement) {
        var isAllLocationSelected = scwin.selectedLocationsArray.indexOf('Ï†ÑÏ≤¥ÏßÄÏó≠') !== -1;
        if (isAllLocationSelected) {
            countElement.textContent = 'Ï†ÑÏ≤¥ ÏßÄÏó≠ ÏÑ†ÌÉùÎê®';
            countElement.style.color = '#00c851';
        } else {
            countElement.textContent = scwin.selectedLocationsArray.length + ' / ' + scwin.maxSelections + ' Í∞ú ÏßÄÏó≠ ÏÑ†ÌÉùÎê®';
            countElement.style.color = '#f44336';
        }
    }
};

// ÏßÄÏó≠ ÏòµÏÖòÎì§ ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
scwin.updateLocationOptions = function() {
    var locationOptions = document.querySelectorAll('.location-option');
    var isAllLocationSelected = scwin.selectedLocationsArray.indexOf('Ï†ÑÏ≤¥ÏßÄÏó≠') !== -1;
    var isMaxReached = scwin.selectedLocationsArray.length >= scwin.maxSelections;

    locationOptions.forEach(function(option) {
        var locationText = option.textContent.trim();
        var isSelected = option.classList.contains('selected');

        if (!isSelected) {
            if (isAllLocationSelected && locationText !== 'Ï†ÑÏ≤¥ ÏßÄÏó≠') {
                // Ï†ÑÏ≤¥ ÏßÄÏó≠Ïù¥ ÏÑ†ÌÉùÎêú Í≤ΩÏö∞ Îã§Î•∏ Î™®Îì† ÏßÄÏó≠ ÎπÑÌôúÏÑ±Ìôî
                option.classList.add('disabled');
            } else if (!isAllLocationSelected && locationText === 'Ï†ÑÏ≤¥ ÏßÄÏó≠' && scwin.selectedLocationsArray.length > 0) {
                // ÌäπÏ†ï ÏßÄÏó≠Ïù¥ ÏÑ†ÌÉùÎêú Í≤ΩÏö∞ Ï†ÑÏ≤¥ ÏßÄÏó≠ ÎπÑÌôúÏÑ±Ìôî
                option.classList.add('disabled');
            } else if (!isAllLocationSelected && locationText !== 'Ï†ÑÏ≤¥ ÏßÄÏó≠' && isMaxReached) {
                // ÌäπÏ†ï ÏßÄÏó≠Îì§Ïù¥ ÏµúÎåÄ Í∞úÏàòÏóê ÎèÑÎã¨Ìïú Í≤ΩÏö∞ ÎÇòÎ®∏ÏßÄ ÌäπÏ†ï ÏßÄÏó≠Îì§ ÎπÑÌôúÏÑ±Ìôî
                option.classList.add('disabled');
            } else {
                option.classList.remove('disabled');
            }
        }
    });
};

// Îã§Ïùå Î≤ÑÌäº ÏÉÅÌÉú ÏóÖÎç∞Ïù¥Ìä∏
scwin.updateNextButton = function() {
    var nextBtn = document.querySelector('.btn-next');

    if (scwin.selectedLocationsArray.length > 0) {
        nextBtn.classList.add('active');
        nextBtn.disabled = false;
    } else {
        nextBtn.classList.remove('active');
        nextBtn.disabled = true;
    }
};

// Ïù¥Ï†Ñ Î≤ÑÌäº ÌÅ¥Î¶≠
scwin.goBack = function() {
    if (confirm("Ïù¥Ï†Ñ Îã®Í≥ÑÎ°ú ÎèåÏïÑÍ∞ÄÏãúÍ≤†ÏäµÎãàÍπå? ÏÑ†ÌÉùÌïòÏã† ÏßÄÏó≠Ïù¥ ÏÇ¨ÎùºÏßà Ïàò ÏûàÏäµÎãàÎã§.")) {
        $p.url("UserDetail.xml");
    }
};

// Îã§Ïùå Î≤ÑÌäº ÌÅ¥Î¶≠ (ÏôÑÎ£å)
scwin.goNext = function() {
    if (scwin.selectedLocationsArray.length === 0) {
        $c.win.alert("Ìù¨Îßù Í∑ºÎ¨¥ÏßÄÏó≠ÏùÑ ÌïòÎÇò Ïù¥ÏÉÅ ÏÑ†ÌÉùÌï¥Ï£ºÏÑ∏Ïöî.");
        return;
    }

    // ÏÇ¨Ïö©Ïûê Ï†ïÎ≥¥ ÏóÖÎç∞Ïù¥Ìä∏
    $c.win.alert("ÏßÄÏó≠ Ï†ïÎ≥¥Í∞Ä ÏÑ±Í≥µÏ†ÅÏúºÎ°ú ÏóÖÎç∞Ïù¥Ìä∏ÎêòÏóàÏäµÎãàÎã§!\\nÏÑ†ÌÉùÎêú ÏßÄÏó≠: " + scwin.selectedLocationsArray.join(', '));

    // ÎßàÏù¥ÌéòÏù¥ÏßÄÎ°ú ÎèåÏïÑÍ∞ÄÍ∏∞
    $p.url("UserDetail.xml");
};
