head.script:
scwin.onpageload = function () {
	var parentOptions = $p.get; // UserDetailMain.xml에서 전달된 dataObject를 가져옵니다.
	if (parentOptions && parentOptions.dataObject && parentOptions.dataObject.mbtiType) {
		var mbtiType = parentOptions.dataObject.mbtiType;
		dma_mbtiResult.set("mbtiType", mbtiType);
		var imageUrl = "/InsWebApp/images/ws5/mbti/png/" + mbtiType + ".png";
		result_image.setSrc(imageUrl);
		result_mbti_type.setLabel(mbtiType);
	}
};
