// Îã§Ïùå Î≤ÑÌäº (ÏôÑÎ£å) - $p.url Î∞©Ïãù
scwin.btn_next_onclick = function () {
    // ÏµúÏã† Í∞íÏúºÎ°ú Îç∞Ïù¥ÌÑ∞ Î™®Îç∏ ÏóÖÎç∞Ïù¥Ìä∏
    var textarea = document.querySelector('.additional-intro-textarea');
    var bio = textarea ? textarea.value : "";
    dma_profile.set("bio", bio);

    var profileImage = dma_profile.get("profileImage");

    console.log("ÏûÖÎ†•Îêú ÌîÑÎ°úÌïÑ Ï†ïÎ≥¥:", {
        profileImage: profileImage,
        bio: bio
    });

    // Î™®Îì† Îã®Í≥ÑÏóêÏÑú ÏûÖÎ†•Îêú Îç∞Ïù¥ÌÑ∞Î•º ÏàòÏßëÌïòÏó¨ ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ°
    scwin.submitAllAdditionalInfo();
};

// Î™®Îì† Ï∂îÍ∞Ä Ï†ïÎ≥¥Î•º ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ°ÌïòÎäî Ìï®Ïàò
scwin.submitAllAdditionalInfo = function () {
    try {
        // ProfileAdditional ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞Îßå Ï≤òÎ¶¨ (Ïù¥Ï†Ñ Îã®Í≥Ñ Îç∞Ïù¥ÌÑ∞Îäî Ïù¥ÎØ∏ Ï†ÄÏû•Îê®)
        var textarea = document.querySelector('.additional-intro-textarea');
        var bio = textarea ? textarea.value : "";
        var profileImage = dma_profile.get("profileImage") || "";

        // ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞Îßå ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ° (Ïù¥Ï†Ñ Îã®Í≥Ñ Îç∞Ïù¥ÌÑ∞Îäî Îπà Í∞íÏúºÎ°ú Ï†ÑÏÜ°ÌïòÏßÄ ÏïäÏùå)
        var requestData = {
            bio: bio,
            profileImageName: profileImage
        };

        console.log("ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞Îßå ÏÑúÎ≤Ñ Ï†ÑÏÜ°:", requestData);

        // ÏÑúÎ≤ÑÏóê AJAX ÏöîÏ≤≠ÏúºÎ°ú ÌòÑÏû¨ ÌéòÏù¥ÏßÄ Îç∞Ïù¥ÌÑ∞Îßå Ï†ÑÏÜ°
        var xhr = new XMLHttpRequest();
        xhr.open('POST', '/InsWebApp/USUpdateAdditionalInfo.pwkjson', true);
        xhr.setRequestHeader('Content-Type', 'application/json');

        xhr.onload = function () {
            if (xhr.status === 200) {
                try {
                    var response = JSON.parse(xhr.responseText);
                    console.log("ÏÑúÎ≤Ñ ÏùëÎãµ:", response);

                    if (response.success) {
                        console.log("Ï∂îÍ∞Ä Ï†ïÎ≥¥ Ï†ÄÏû• ÏÑ±Í≥µ");
                        // ÌîÑÎ°úÌïÑ ÏûëÏÑ± ÏôÑÎ£å ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
                        $p.url("ProfileComplete.xml");
                    } else {
                        $c.win.alert("Ï†ïÎ≥¥ Ï†ÄÏû• Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§: " + response.message);
                    }
                } catch (e) {
                    $c.win.alert("ÏÑúÎ≤Ñ ÏùëÎãµ Ï≤òÎ¶¨ Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
                }
            } else {
                $c.win.alert("ÏÑúÎ≤Ñ ÌÜµÏã† Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
            }
        };

        xhr.onerror = function () {
            $c.win.alert("ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        };

        xhr.send(JSON.stringify(requestData));

    } catch (e) {
        $c.win.alert("Îç∞Ïù¥ÌÑ∞ Ï†ÑÏÜ° Ï§ë Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
        // Ïò§Î•òÍ∞Ä Î∞úÏÉùÌï¥ÎèÑ ÏùºÎã® Îã§Ïùå ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô
        $p.url("ProfileComplete.xml");
    }
};
